      expect(result).toEqual({
        success: true,
        message: `Release ${mockReleaseId} published successfully`,
        releaseId: mockReleaseId,
        documentCount: 1,
        result: mockResult
      });
    });

    it('should throw error if release has too many documents', async () => {
      // Generate 51 mock documents to exceed the 50 document limit
      const mockDocs = Array.from({ length: 51 }).map((_, i) => ({
        _id: `versions.${mockReleaseId}.doc${i}`,
        _type: 'post',
        title: `Post ${i}`
      }));

      mockClient.fetch.mockResolvedValueOnce(mockDocs);

      await expect(
        releasesController.publishRelease(mockProjectId, mockDataset, mockReleaseId)
      ).rejects.toThrow(/Release contains 51 documents, which exceeds the 50 document limit/);

      expect(sanityApi.performActions).not.toHaveBeenCalled();
    });
  });

  describe('listReleases', () => {
    it('should list all releases', async () => {
      const mockReleases = [
        { _id: '_.releases.rel1', name: 'rel1', state: 'active' },
        { _id: '_.releases.rel2', name: 'rel2', state: 'published' }
      ];

      mockClient.fetch.mockResolvedValueOnce(mockReleases);

      const result = await releasesController.listReleases(
        mockProjectId,
        mockDataset
      );

      expect(mockClient.fetch).toHaveBeenCalledWith('releases::all()');
      expect(result).toEqual({
        releases: mockReleases
      });
    });
  });

  describe('updateRelease', () => {
    it('should update a release with provided fields', async () => {
      const mockUpdate = {
        title: 'Updated Title',
        description: 'Updated description'
      };
      const mockResult = { transactionId: 'tx123' };

      (sanityApi.performActions as any).mockResolvedValueOnce(mockResult);

      const result = await releasesController.updateRelease(
        mockProjectId,
        mockDataset,
        mockReleaseId,
        mockUpdate
      );

      expect(sanityApi.performActions).toHaveBeenCalledWith(
        mockProjectId,
        mockDataset,
        [
          {
            actionType: 'sanity.action.release.edit',
            releaseId: mockReleaseId,
            patch: {
              id: mockReleaseId,
              set: {
                metadata: {
                  title: 'Updated Title',
                  description: 'Updated description'
                }
              }
            }
          }
        ]
      );

      expect(result.success).toBe(true);
    });
  });

  describe('removeDocumentFromRelease', () => {
    it('should remove a single document from a release', async () => {
      const mockResult = { transactionId: 'tx123' };

      (sanityApi.performActions as any).mockResolvedValueOnce(mockResult);

      const result = await releasesController.removeDocumentFromRelease(
        mockProjectId,
        mockDataset,
        mockReleaseId,
        mockDocumentId
      );

      expect(sanityApi.performActions).toHaveBeenCalledWith(
        mockProjectId,
        mockDataset,
        [
          {
            actionType: 'sanity.action.document.delete',
            documentId: `versions.${mockReleaseId}.${mockDocumentId}`
          }
        ]
      );

      expect(result).toEqual({
        success: true,
        message: `1 document(s) removed from release ${mockReleaseId} successfully`,
        releaseId: mockReleaseId,
        documentIds: [mockDocumentId],
        result: mockResult
      });
    });

    it('should remove multiple documents from a release when given an array of IDs', async () => {
      const mockDocumentIds = ['doc1', 'doc2', 'doc3'];
      const mockResult = { transactionId: 'tx123' };

      // Mock the performActions function to return a single result
      (sanityApi.performActions as any).mockResolvedValueOnce(mockResult);

      const result = await releasesController.removeDocumentFromRelease(
        mockProjectId,
        mockDataset,
        mockReleaseId,
        mockDocumentIds
      );

      // Verify that performActions was called once with all document actions
      expect(sanityApi.performActions).toHaveBeenCalledTimes(1);

      // Check that all document IDs were included in a single call
      expect(sanityApi.performActions).toHaveBeenCalledWith(
        mockProjectId,
        mockDataset,
        [
          {
            actionType: 'sanity.action.document.delete',
            documentId: `versions.${mockReleaseId}.${mockDocumentIds[0]}`
          },
          {
            actionType: 'sanity.action.document.delete',
            documentId: `versions.${mockReleaseId}.${mockDocumentIds[1]}`
          },
          {
            actionType: 'sanity.action.document.delete',
            documentId: `versions.${mockReleaseId}.${mockDocumentIds[2]}`
          }
        ]
      );

      // Verify the result structure
      expect(result).toEqual({
        success: true,
        message: `${mockDocumentIds.length} document(s) removed from release ${mockReleaseId} successfully`,
        releaseId: mockReleaseId,
        documentIds: mockDocumentIds,
        result: mockResult
      });
    });

    it('should handle errors when removing a document from a release', async () => {
      const mockError = new Error('Version deletion failed');

      (sanityApi.performActions as any).mockRejectedValueOnce(mockError);

      await expect(
        releasesController.removeDocumentFromRelease(
          mockProjectId,
          mockDataset,
          mockReleaseId,
          mockDocumentId
        )
      ).rejects.toThrow(`Failed to remove document(s) from release ${mockReleaseId}: Version deletion failed`);

      expect(sanityApi.performActions).toHaveBeenCalled();
    });
  });

  describe('getRelease', () => {
    it('should get a release by ID', async () => {
      const mockReleaseData = {
        _id: `_.releases.${mockReleaseId}`,
        title: 'Test Release',
        status: 'active'
      };

      mockClient.fetch.mockResolvedValueOnce([mockReleaseData]);

      const result = await releasesController.getRelease(
        mockProjectId,
        mockDataset,
        mockReleaseId
      );

      expect(mockClient.fetch).toHaveBeenCalledWith(
        `*[_id == "_.releases.${mockReleaseId}"]`
      );

      expect(result).toEqual({
        release: mockReleaseData
      });
    });

    it('should throw an error when release is not found', async () => {
      mockClient.fetch.mockResolvedValueOnce([]);

      await expect(
        releasesController.getRelease(
          mockProjectId,
          mockDataset,
          mockReleaseId
        )
      ).rejects.toThrow('Failed to get release: Release with ID');
    });
  });
});

import { describe, it, expect, vi, beforeEach } from 'vitest';
import * as fs from 'fs/promises';
import * as schemaController from '../../src/controllers/schema.js';
import config from '../../src/config/config.js';

vi.mock('fs/promises');
vi.mock('../../src/config/config.js', () => ({
  default: {
    getSchemaPath: vi.fn(),
  }
}));

describe('Schema Controller', () => {
  // Define mock schema for testing
  const mockSchema = [
    {
      name: 'author',
      type: 'document',
      fields: [
        { name: 'name', type: 'string' },
        { name: 'bio', type: 'text' }
      ]
    },
    {
      name: 'post',
      type: 'document',
      fields: [
        { name: 'title', type: 'string' },
        { 
          name: 'author', 
          type: 'reference', 
          to: { type: 'author' } 
        },
        {
          name: 'categories',
          type: 'array',
          of: [{ type: 'reference', to: { type: 'category' } }]
        },
        {
          name: 'content',
          type: 'array',
          of: [{ type: 'block' }]
        }
      ]
    },
    {
      name: 'category',
      type: 'type',
      fields: [
        { name: 'title', type: 'string' }
      ]
    },
    {
      name: 'block',
      type: 'type',
      fields: [
        { name: 'text', type: 'string' }
      ]
    }
  ];

  beforeEach(() => {
    vi.resetAllMocks();
    vi.spyOn(console, 'error').mockImplementation(() => {});
    vi.mocked(config.getSchemaPath).mockReturnValue('/mock/path/to/schema.json');
    vi.mocked(fs.readFile).mockResolvedValue(JSON.stringify(mockSchema));
  });

  describe('getSchema', () => {
    it('should return schema from file if it exists', async () => {
      const result = await schemaController.getSchema('mock-project', 'mock-dataset');

      expect(result).toEqual(mockSchema);
      expect(config.getSchemaPath).toHaveBeenCalledWith('mock-project', 'mock-dataset');
      expect(fs.readFile).toHaveBeenCalledWith('/mock/path/to/schema.json', 'utf-8');
    });

    it('should throw an error if schema file does not exist', async () => {
      const error = new Error('File not found') as NodeJS.ErrnoException;
      error.code = 'ENOENT';

      vi.mocked(fs.readFile).mockRejectedValueOnce(error);

      await expect(schemaController.getSchema('mock-project', 'mock-dataset'))
        .rejects
        .toThrow('Schema file not found for project mock-project and dataset mock-dataset');
    });

    it('should throw on other file read errors', async () => {
      const error = new Error('Some other error');
      vi.mocked(fs.readFile).mockRejectedValueOnce(error);

      await expect(schemaController.getSchema('mock-project', 'mock-dataset'))
        .rejects
        .toThrow('Failed to get schema:');
    });
  });

  describe('listSchemaTypes', () => {
    it('should list only document types by default', async () => {
      const result = await schemaController.listSchemaTypes('mock-project', 'mock-dataset');

      // Should only include document types
      expect(result).toEqual([
        { name: 'author', type: 'document' },
        { name: 'post', type: 'document' }
      ]);
    });

    it('should list all types when allTypes option is true', async () => {
      const result = await schemaController.listSchemaTypes('mock-project', 'mock-dataset', { allTypes: true });

      // Should include all types
      expect(result).toEqual([
        { name: 'author', type: 'document' },
        { name: 'post', type: 'document' },
        { name: 'category', type: 'type' },
        { name: 'block', type: 'type' }
      ]);
    });

    it('should catch and rethrow errors', async () => {
      const error = new Error('Schema error');
      vi.mocked(fs.readFile).mockRejectedValueOnce(error);

      await expect(schemaController.listSchemaTypes('mock-project', 'mock-dataset'))
        .rejects
        .toThrow('Failed to list schema types:');

      expect(console.error).toHaveBeenCalled();
    });
  });

  describe('getTypeSchema', () => {
    it('should return the schema for a document type', async () => {
      const result = await schemaController.getTypeSchema('mock-project', 'mock-dataset', 'author');

      expect(result.name).toBe('author');
      expect(result.type).toBe('document');
    });

    it('should return the schema for a custom type', async () => {
      const result = await schemaController.getTypeSchema('mock-project', 'mock-dataset', 'category');

      expect(result.name).toBe('category');
      expect(result.type).toBe('type');
    });

    it('should throw an error if type is not found', async () => {
      await expect(schemaController.getTypeSchema('mock-project', 'mock-dataset', 'nonexistent'))
        .rejects
        .toThrow("Type 'nonexistent' not found in schema");
    });
  });

  describe('getSchemaForType', () => {
    it('should get document schema for a type', async () => {
      const result = await schemaController.getSchemaForType('mock-project', 'mock-dataset', 'author');

      expect(result.name).toBe('author');
      expect(result.type).toBe('document');
    });

    it('should get non-document schema for a type', async () => {
      const result = await schemaController.getSchemaForType('mock-project', 'mock-dataset', 'category');

      expect(result.name).toBe('category');
      expect(result.type).toBe('type');
    });

    it('should include referenced types when includeReferences is true', async () => {
      const result = await schemaController.getSchemaForType('mock-project', 'mock-dataset', 'post', { includeReferences: true });

      expect(result.name).toBe('post');
      expect(result.references).toBeDefined();
      expect(result.references.length).toBeGreaterThan(0);

      // Check that referenced types are included
      const referenceNames = result.references.map(ref => ref.name);
      expect(referenceNames).toContain('author');
      expect(referenceNames).toContain('category');
    });

    it('should throw error when type is not found', async () => {
      await expect(schemaController.getSchemaForType('mock-project', 'mock-dataset', 'nonexistent'))
        .rejects
        .toThrow('Type nonexistent not found in schema');
    });
  });
});

/**
 * Integration test for the full release and document workflow
 * Tests creation of releases, documents, adding versions, and cleanup
 */
import { describe, it, expect, beforeAll, afterAll } from 'vitest';
import dotenv from 'dotenv';
import { v4 as uuidv4 } from 'uuid';
import * as actionsController from '../../src/controllers/actions.js';
import * as releasesController from '../../src/controllers/releases.js';
import { createSanityClient } from '../../src/utils/sanityClient.js';

// Load environment variables
dotenv.config();

describe('Release and Document Workflow Integration', () => {
  // Configuration from environment
  const projectId = process.env.SANITY_PROJECT_ID || '';
  const dataset = process.env.SANITY_DATASET || 'production';

  // Test identifiers with unique IDs to avoid conflicts
  const testPrefix = 'integration-test';
  const releaseId = `${testPrefix}-release-${uuidv4().substring(0, 8)}`;
  const documentId = `${testPrefix}-doc-${uuidv4().substring(0, 8)}`;
  const draftDocumentId = `drafts.${documentId}`;
  const documentId2 = `${testPrefix}-doc2-${uuidv4().substring(0, 8)}`;
  const documentId3 = `${testPrefix}-doc3-${uuidv4().substring(0, 8)}`;
  const draftDocumentId2 = `drafts.${documentId2}`;
  const draftDocumentId3 = `drafts.${documentId3}`;

  // Set a longer timeout for integration tests
  const INTEGRATION_TIMEOUT = 15000;

  // Track created resources for cleanup
  const resourcesToCleanup = {
    releaseId: '',
    documentId: '',
    documentVersionId: '',
    documentId2: '',
    documentId3: '',
    documentVersionIds: [] as string[]
  };

  // Skip tests if environment is not properly configured
  beforeAll(() => {
    if (!projectId) {
      throw new Error('SANITY_PROJECT_ID environment variable is required for this test');
    }
  }, INTEGRATION_TIMEOUT);

  // Clean up all resources after tests
  afterAll(async () => {
    // Only attempt cleanup if resources were created
    try {
      // Clean up document version if it was created
      if (resourcesToCleanup.documentVersionId) {
        await actionsController.discardDocumentVersion(
          projectId, 
          dataset,
          resourcesToCleanup.documentVersionId
        );
        console.log(`Cleaned up document version: ${resourcesToCleanup.documentVersionId}`);
      }

      // Clean up document if it was created
      if (resourcesToCleanup.documentId) {
        // Use direct client delete since our IDs might not be correct
        const client = createSanityClient(projectId, dataset);
        try {
          // Try to delete both the draft and published versions
          await client.delete(documentId);
          console.log(`Cleaned up published document: ${documentId}`);
        } catch (err) {
          console.log(`No published document to clean up: ${documentId}`);
        }

        try {
          await client.delete(draftDocumentId);
          console.log(`Cleaned up draft document: ${draftDocumentId}`);
        } catch (err) {
          console.log(`No draft document to clean up: ${draftDocumentId}`);
        }

        // Clean up additional documents
        if (resourcesToCleanup.documentId2) {
          try {
            await client.delete(documentId2);
            console.log(`Cleaned up published document: ${documentId2}`);
          } catch (err) {
            console.log(`No published document to clean up: ${documentId2}`);
          }

          try {
            await client.delete(draftDocumentId2);
            console.log(`Cleaned up draft document: ${draftDocumentId2}`);
          } catch (err) {
            console.log(`No draft document to clean up: ${draftDocumentId2}`);
          }
        }

        if (resourcesToCleanup.documentId3) {
          try {
            await client.delete(documentId3);
            console.log(`Cleaned up published document: ${documentId3}`);
          } catch (err) {
            console.log(`No published document to clean up: ${documentId3}`);
          }

          try {
            await client.delete(draftDocumentId3);
            console.log(`Cleaned up draft document: ${draftDocumentId3}`);
          } catch (err) {
            console.log(`No draft document to clean up: ${draftDocumentId3}`);
          }
        }
      }

      // Clean up release if it was created
      if (resourcesToCleanup.releaseId) {
        // First archive the release (required before deletion)
        await releasesController.archiveRelease(
          projectId,
          dataset,
          resourcesToCleanup.releaseId
        );

        // Then delete the archived release
        await releasesController.deleteRelease(
          projectId,
          dataset,
          resourcesToCleanup.releaseId
        );
        console.log(`Cleaned up release: ${resourcesToCleanup.releaseId}`);
      }
    } catch (error) {
      console.error('Error during cleanup:', error);
    }
  }, INTEGRATION_TIMEOUT);

  it('should create a release', async () => {
    // Create a new release
    const result = await releasesController.createRelease(
      projectId,
      dataset,
      releaseId,
      `Integration Test Release ${new Date().toISOString()}`,
      {
        description: 'Created by integration tests - will be auto-cleaned up',
        releaseType: 'asap'
      }
    );

    // Store ID for cleanup
    resourcesToCleanup.releaseId = releaseId;

    // Verify the release was created successfully
    expect(result.success).toBe(true);
    expect(result.releaseId).toBe(releaseId);
  }, INTEGRATION_TIMEOUT);

  it('should create a document', async () => {
    // Create a test document with direct client to ensure success
    const client = createSanityClient(projectId, dataset);

    const testDocument = {
      _id: documentId,
      _type: 'test',
      title: 'Integration Test Document',
      description: 'Created by integration tests - will be auto-cleaned up',
      createdAt: new Date().toISOString()
    };

    // Create the document directly using the client
    const createdDoc = await client.createOrReplace(testDocument);

    // Store the document ID for cleanup
    resourcesToCleanup.documentId = documentId;

    // Verify the document was created
    expect(createdDoc._id).toBe(documentId);
  }, INTEGRATION_TIMEOUT);

  it('should add document to a release', async () => {
    // Use the releases controller to add the document to the release
    const result = await releasesController.addDocumentToRelease(
      projectId,
      dataset,
      releaseId,
      documentId
    );

    // Verify the document was added to the release
    expect(result.success).toBe(true);
    expect(result.releaseId).toBe(releaseId);
    expect(result.documentIds).toContain(documentId);

    // Store the version ID for cleanup
    if (result.versionIds && result.versionIds.length > 0) {
      resourcesToCleanup.documentVersionId = result.versionIds[0];
    }

    // Wait briefly to ensure document is processed
    await new Promise(resolve => setTimeout(resolve, 1000));
  }, INTEGRATION_TIMEOUT);

  it('should list documents in a release', async () => {
    // Get list of documents in the release
    const result = await releasesController.listReleaseDocuments(
      projectId,
      dataset,
      releaseId
    );

    // Verify our document is in the release
    expect(result.releaseId).toBe(releaseId);
    expect(result.documentCount).toBeGreaterThan(0);

    // Find our document in the list
    const ourDocument = result.documents.find(doc => doc.documentId === documentId);
    expect(ourDocument).toBeDefined();
  }, INTEGRATION_TIMEOUT);

  it('should update a document', async () => {
    // Direct client update instead of patch to ensure success
    const client = createSanityClient(projectId, dataset);

    // Fetch the current document
    const currentDocument = await client.getDocument(documentId);
    expect(currentDocument).toBeTruthy();

    // Update fields
    const updatedDocument = {
      ...currentDocument,
      updatedAt: new Date().toISOString(),
      status: 'updated',
      _id: documentId // Ensure _id is explicitly set
    };

    // Replace the document
    const result = await client.createOrReplace(updatedDocument);

    // Verify the document was updated
    expect(result._id).toBe(documentId);
    expect(result.status).toBe('updated');
  }, INTEGRATION_TIMEOUT);

  it('should create a document version with updated content', async () => {
    // Create a new version document with custom content
    const customContent = {
      _id: documentId,
      _type: 'test',
      title: 'Updated Document Title',
      description: 'This content is provided explicitly rather than fetched',
      updatedAt: new Date().toISOString(),
      status: 'manually-specified'
    };

    // Create a new version of the document in the release
    const result = await actionsController.createDocumentVersion(
      projectId,
      dataset,
      releaseId,
      documentId,
      customContent
    );

    // Update version ID for cleanup if needed
    if (result.versionId) {
      resourcesToCleanup.documentVersionId = result.versionId;
    }

    // Verify the version was created
    expect(result.success).toBe(true);
    expect(result.versionId).toBeTruthy();
  }, INTEGRATION_TIMEOUT);

  it('should create additional test documents', async () => {
    // Create two additional test documents for the array test
    const client = createSanityClient(projectId, dataset);

    const testDocument2 = {
      _id: documentId2,
      _type: 'test',
      title: 'Integration Test Document 2',
      description: 'Second test document for array testing',
      createdAt: new Date().toISOString()
    };

    const testDocument3 = {
      _id: documentId3,
      _type: 'test',
      title: 'Integration Test Document 3',
      description: 'Third test document for array testing',
      createdAt: new Date().toISOString()
    };

    // Create the documents
    const createdDoc2 = await client.createOrReplace(testDocument2);
    const createdDoc3 = await client.createOrReplace(testDocument3);

    // Store the document IDs for cleanup
    resourcesToCleanup.documentId2 = documentId2;
    resourcesToCleanup.documentId3 = documentId3;

    // Verify the documents were created
    expect(createdDoc2._id).toBe(documentId2);
    expect(createdDoc3._id).toBe(documentId3);
  }, INTEGRATION_TIMEOUT);

  it('should add multiple documents to a release as an array', async () => {
    // Add both documents to the release using the array parameter
    const result = await releasesController.addDocumentToRelease(
      projectId,
      dataset,
      releaseId,
      [documentId2, documentId3]
    );

    // Verify the documents were added to the release
    expect(result.success).toBe(true);
    expect(result.releaseId).toBe(releaseId);
    expect(result.documentIds).toContain(documentId2);
    expect(result.documentIds).toContain(documentId3);

    // Store version IDs for cleanup
    if (result.versionIds && result.versionIds.length > 0) {
      resourcesToCleanup.documentVersionIds = result.versionIds;
    }

    // Wait briefly to ensure documents are processed
    await new Promise(resolve => setTimeout(resolve, 1000));
  }, INTEGRATION_TIMEOUT);

  it('should list all documents in the release including the array-added ones', async () => {
    // Get list of documents in the release
    const result = await releasesController.listReleaseDocuments(
      projectId,
      dataset,
      releaseId
    );

    // Verify all three documents are in the release
    expect(result.releaseId).toBe(releaseId);
    expect(result.documentCount).toBeGreaterThanOrEqual(3);

    // Find our documents in the list
    const document1 = result.documents.find(doc => doc.documentId === documentId);
    const document2 = result.documents.find(doc => doc.documentId === documentId2);
    const document3 = result.documents.find(doc => doc.documentId === documentId3);

    expect(document1).toBeDefined();
    expect(document2).toBeDefined();
    expect(document3).toBeDefined();
  }, INTEGRATION_TIMEOUT);
});

/**
 * Tools Registry Tests
 * 
 * Tests the tools registry functionality and individual tool definitions
 */
import { describe, it, expect, beforeAll } from 'vitest';
import * as toolsRegistry from '../../src/tools/index.js';
import { SchemaToolProvider } from '../../src/tools/schemaTools.js';
import { MutateToolProvider } from '../../src/tools/mutateTools.js';
import { EmbeddingsToolProvider } from '../../src/tools/embeddingsTools.js';
import { GroqToolProvider } from '../../src/tools/groqTools.js';

describe('Tools', () => {
  let allTools;
  let schemaTools;
  let mutateTools;
  let embeddingsTools;
  let groqTools;
  let getTypeSchemaTool;
  let mutateDocumentTool;
  let executeGroqTool;
  let semanticSearchTool;

  beforeAll(() => {
    // Get all tools
    allTools = toolsRegistry.getToolDefinitions();

    // Get individual provider's tools
    schemaTools = new SchemaToolProvider().getToolDefinitions();
    mutateTools = new MutateToolProvider().getToolDefinitions();
    embeddingsTools = new EmbeddingsToolProvider().getToolDefinitions();
    groqTools = new GroqToolProvider().getToolDefinitions();

    // Find specific tools
    getTypeSchemaTool = schemaTools.find(tool => tool.name === 'getTypeSchema');
    mutateDocumentTool = mutateTools.find(tool => tool.name === 'mutateDocument');
    executeGroqTool = groqTools.find(tool => tool.name === 'executeGroq');
    semanticSearchTool = embeddingsTools.find(tool => tool.name === 'semanticSearch');
  });

  describe('getToolDefinitions', () => {
    it('should return an array of tool definitions', () => {
      expect(Array.isArray(allTools)).toBe(true);
      expect(allTools.length).toBeGreaterThan(0);
    });

    it('should include schema tools', () => {
      // Check if schema tools are included in the main tool registry
      const hasSchemaTools = allTools.some(tool => 
        tool.name === 'getTypeSchema'
      );
      expect(hasSchemaTools).toBe(true);

      // Additionally validate that the schema tool provider returns this tool
      expect(getTypeSchemaTool).toBeDefined();
    });

    it('should include mutate tools', () => {
      // Check if mutate tools are included in the main tool registry
      const hasMutateTools = allTools.some(tool => 
        tool.name === 'mutateDocument'
      );
      expect(hasMutateTools).toBe(true);

      // Additionally validate that the mutate tool provider returns this tool
      expect(mutateDocumentTool).toBeDefined();
    });

    it('should include GROQ tools', () => {
      // Check if GROQ tools are included in the main tool registry
      const hasGroqTools = allTools.some(tool => 
        tool.name === 'executeGroq'
