import { createSanityClient, sanityApi } from '../utils/sanityClient.js';
import { 
  normalizeDraftId, 
  normalizeBaseDocId, 
  applyPatchOperations, 
  getDocumentContent,
  createErrorResponse,
  normalizeDocumentIds
} from '../utils/documentHelpers.js';
