import { createSanityClient } from '../utils/sanityClient.js';
import { SanityClient, SanityError, SanityDocument, InsertOperation, PatchOperations } from '../types/sanity.js';

// Define interface for Sanity transaction
interface SanityTransaction {
  create: (document: Record<string, any>) => SanityTransaction;
  createOrReplace: (document: Record<string, any>) => SanityTransaction;
  createIfNotExists: (document: Record<string, any>) => SanityTransaction;
  delete: (idOrQuery: string) => SanityTransaction;
  patch: (patchObj: SanityPatch | Record<string, any>) => SanityTransaction;
  commit: () => Promise<Record<string, any>>;
}

// Define interface for Sanity patch
interface SanityPatch {
  set: (fields: Record<string, any>) => SanityPatch;
  setIfMissing: (fields: Record<string, any>) => SanityPatch;
  unset: (fields: string[]) => SanityPatch;
  inc: (fields: Record<string, number>) => SanityPatch;
  dec: (fields: Record<string, number>) => SanityPatch;
  insert: (position: 'before' | 'after' | 'replace', path: string, items: any | any[]) => SanityPatch;
  ifRevisionId: (revisionId: string) => SanityPatch;
}

// Define types for mutations
interface SanityDocumentStub<T extends { _type: string }> {
  _type: string;
  [key: string]: any;
}

interface IdentifiedSanityDocumentStub<T extends Record<string, any>> extends SanityDocumentStub<T & { _type: string }> {
  _id: string;
}

export interface CreateMutation {
  create: SanityDocumentStub<{ _type: string }>;
}

export interface CreateOrReplaceMutation {
  createOrReplace: IdentifiedSanityDocumentStub<Record<string, any>>;
}

export interface CreateIfNotExistsMutation {
  createIfNotExists: IdentifiedSanityDocumentStub<Record<string, any>>;
}

export interface DeleteByIdMutation {
  delete: {
    id: string;
  };
}

export interface DeleteByQueryMutation {
  delete: {
    query: string;
    params?: Record<string, any>;
  };
}

export interface PatchByIdMutation {
  patch: {
    id: string;
    ifRevisionID?: string;
    set?: Record<string, any>;
    setIfMissing?: Record<string, any>;
    unset?: string | string[];
    inc?: Record<string, number>;
    dec?: Record<string, number>;
    insert?: InsertOperation;
    diffMatchPatch?: Record<string, string>;
  };
}

export interface PatchByQueryMutation {
  patch: {
    query: string;
    params?: Record<string, any>;
    set?: Record<string, any>;
    setIfMissing?: Record<string, any>;
    unset?: string | string[];
    inc?: Record<string, number>;
    dec?: Record<string, number>;
    insert?: InsertOperation;
    diffMatchPatch?: Record<string, string>;
  };
}

export type Mutation = 
  | CreateMutation 
  | CreateOrReplaceMutation 
  | CreateIfNotExistsMutation 
  | DeleteByIdMutation 
  | DeleteByQueryMutation 
  | PatchByIdMutation 
  | PatchByQueryMutation;

/**
 * Helper function to add create mutation to a transaction
 * 
 * @param transaction - Sanity transaction
 * @param mutation - Create mutation
 */
function addCreateMutation(
  transaction: SanityTransaction, 
  mutation: CreateMutation
): void {
  transaction.create(mutation.create);
}

/**
 * Helper function to add createOrReplace mutation to a transaction
 * 
 * @param transaction - Sanity transaction
 * @param mutation - CreateOrReplace mutation
 */
function addCreateOrReplaceMutation(
  transaction: SanityTransaction, 
  mutation: CreateOrReplaceMutation
): void {
  transaction.createOrReplace(mutation.createOrReplace);
}

/**
 * Helper function to add createIfNotExists mutation to a transaction
 * 
 * @param transaction - Sanity transaction
 * @param mutation - CreateIfNotExists mutation
 */
function addCreateIfNotExistsMutation(
  transaction: SanityTransaction, 
  mutation: CreateIfNotExistsMutation
): void {
  transaction.createIfNotExists(mutation.createIfNotExists);
}

/**
 * Helper function to add delete mutation to a transaction
 * 
 * @param transaction - Sanity transaction
 * @param mutation - Delete mutation (by ID or query)
 */
function addDeleteMutation(
  transaction: SanityTransaction, 
  mutation: DeleteByIdMutation | DeleteByQueryMutation
): void {
  if ('query' in mutation.delete) {
    // Delete by query
    transaction.delete(mutation.delete.query);
  } else if ('id' in mutation.delete) {
    // Delete by ID
    transaction.delete(mutation.delete.id);
  }
}

/**
 * Helper function to add patch mutation by ID to a transaction
 * 
 * @param client - Sanity client
 * @param transaction - Sanity transaction
 * @param id - Document ID
 * @param ifRevisionID - Optional revision ID for optimistic locking
 * @param patchOperations - Operations to apply in the patch
 */
function addPatchByIdMutation(
  client: SanityClient,
  transaction: SanityTransaction,
  id: string,
  ifRevisionID?: string,
  patchOperations?: PatchOperations
): void {
  if (!patchOperations) return;

  const patch = client.patch(id);

  // Apply optimistic locking if ifRevisionID is provided
  if (ifRevisionID) {
    patch.ifRevisionId(ifRevisionID);
  }

  applyPatchOperationsToClient(patch, patchOperations);

  // Add the patch to the transaction
  transaction.patch(patch);
}

/**
 * Helper function to add patch mutation by query to a transaction
 * 
 * @param transaction - Sanity transaction
 * @param query - GROQ query string
 * @param params - Optional query parameters
 * @param patchOperations - Operations to apply in the patch
 */
function addPatchByQueryMutation(
  transaction: SanityTransaction,
  query: string,
  params?: Record<string, any>,
  patchOperations?: PatchOperations
): void {
  if (!patchOperations) return;

  transaction.patch({
    query,
    params,
    ...patchOperations
  });
}

/**
 * Helper function to apply patch operations to a Sanity patch client
 * 
 * @param patch - Sanity patch client
 * @param patchOperations - Operations to apply
 */
function applyPatchOperationsToClient(
  patch: SanityPatch,
  patchOperations: PatchOperations
): void {
  // Apply patch operations in the correct order: set, setIfMissing, unset, inc, dec, insert
  if (patchOperations.set) {
    patch.set(patchOperations.set);
  }

  if (patchOperations.setIfMissing) {
    patch.setIfMissing(patchOperations.setIfMissing);
  }

  if (patchOperations.unset) {
    patch.unset(Array.isArray(patchOperations.unset) ? patchOperations.unset : [patchOperations.unset]);
  }

  if (patchOperations.inc) {
    patch.inc(patchOperations.inc);
  }

  if (patchOperations.dec) {
    patch.dec(patchOperations.dec);
  }

  if (patchOperations.insert) {
    const insertOp = patchOperations.insert;
    const { items, position, at } = insertOp;
    if (items && position && at) {
      patch.insert(position, at, items);
    }
  }
}

/**
 * Helper function to apply insert operation to a patch
 * 
 * @param patch - Sanity patch client
 * @param insertOp - Insert operation details
 */
function applyInsertOperation(patch: SanityPatch, insertOp: InsertOperation): void {
  const { items, position, at } = insertOp;
  if (position === 'before' || position === 'after' || position === 'replace') {
    patch.insert(position, at, items);
  }
}

/**
 * Helper function to process a single mutation and add it to the transaction
 * 
 * @param client - Sanity client
 * @param transaction - Sanity transaction
 * @param mutation - Mutation to process
 */
function processMutation(
  client: SanityClient,
  transaction: SanityTransaction,
  mutation: Mutation
): void {
  // Handle create mutation
  if ('create' in mutation) {
    addCreateMutation(transaction, mutation);
  }

  // Handle createOrReplace mutation
  if ('createOrReplace' in mutation) {
    addCreateOrReplaceMutation(transaction, mutation);
  }

  // Handle createIfNotExists mutation
  if ('createIfNotExists' in mutation) {
    addCreateIfNotExistsMutation(transaction, mutation);
  }

  // Handle delete mutation
  if ('delete' in mutation) {
    addDeleteMutation(transaction, mutation);
  }

  // Handle patch mutation
  if ('patch' in mutation) {
    const { id, query, params, ifRevisionID, ...patchOperations } = mutation.patch as PatchOperations & { id?: string; query?: string; params?: Record<string, any>; ifRevisionID?: string };

    if (query) {
      // Patch by query
      addPatchByQueryMutation(transaction, query, params, patchOperations);
    } else if (id) {
      // Patch by ID
      addPatchByIdMutation(client, transaction, id, ifRevisionID, patchOperations);
    }
  }
}

/**
 * Helper function to retrieve documents for mutations
 * 
 * @param client - Sanity client
 * @param mutations - Array of mutations
 * @returns Array of retrieved documents
 */
async function retrieveDocumentsForMutations(
  client: SanityClient,
  mutations: Mutation[]
): Promise<(SanityDocument | null)[]> {
  return await Promise.all(mutations.map(async (mutation) => {
    // Type guard for different mutation types
    if ('create' in mutation) {
      return client.getDocument(mutation.create._id);
    } else if ('createOrReplace' in mutation) {
      return client.getDocument(mutation.createOrReplace._id);
    } else if ('createIfNotExists' in mutation) {
      return client.getDocument(mutation.createIfNotExists._id);
    } else if ('patch' in mutation) {
      // Need to handle both patch by ID and patch by query
      if ('id' in mutation.patch) {
        return client.getDocument(mutation.patch.id);
      } else if ('query' in mutation.patch) {
        // For query-based patches we can't easily get the document
        // without executing the query again
        return null;
      }
    }
    return null;
  }));
}

/**
 * Interface for results returned by modifyDocuments
 */
interface MutateDocumentsResult {
  success: boolean;
  message: string;
  result: Record<string, any>;
  documents?: SanityDocument[];
}

/**
 * Creates or updates documents using Sanity mutations
 * 
 * @param projectId - Sanity project ID
 * @param dataset - Dataset name
 * @param mutations - Array of mutation objects following Sanity mutation format
 * @param returnDocuments - Whether to return modified documents in response
 * @returns Result of the mutations operation
 */
async function modifyDocuments(
  projectId: string, 
  dataset: string, 
  mutations: Mutation[],
  returnDocuments: boolean = false
): Promise<MutateDocumentsResult> {
  try {
    const client = createSanityClient(projectId, dataset);

    // Validate inputs
    if (!mutations || !Array.isArray(mutations) || mutations.length === 0) {
      throw new Error('At least one mutation is required');
    }

    // Create a transaction
    const transaction = client.transaction();

    // Process each mutation
    mutations.forEach(mutation => {
      processMutation(client, transaction, mutation);
    });

    // Commit the transaction
    const result = await transaction.commit();

    if (returnDocuments) {
      const documents = await retrieveDocumentsForMutations(client, mutations);

      return {
        success: true,
        message: `Successfully applied ${mutations.length} mutations`,
        result,
        documents: documents.filter(Boolean) as SanityDocument[]
      };
    } else {
      return {
        success: true,
        message: `Successfully applied ${mutations.length} mutations`,
        result
      };
    }
  } catch (error: unknown) {
    const errorMessage = error instanceof Error ? error.message : String(error);
    console.error(`Error modifying documents:`, error);
    throw new Error(`Failed to modify documents: ${errorMessage}`);
  }
}

/**
 * Helper function to construct patch operations
 * 
 * @param patch - Raw patch object with operations
 * @returns Formatted patch operations
 */
function constructPatchOperations(patch: Record<string, any>): PatchOperations {
  // Extract operations from the patch object
  const operations: PatchOperations = {};

  // Handle 'set' operations
  if (patch.set) {
    operations.set = patch.set;
  }

  // Handle 'setIfMissing' operations
  if (patch.setIfMissing) {
    operations.setIfMissing = patch.setIfMissing;
  }

  // Handle 'unset' operations
  if (patch.unset) {
    operations.unset = Array.isArray(patch.unset) ? patch.unset : [patch.unset];
  }

  // Handle 'inc' operations
  if (patch.inc) {
    operations.inc = patch.inc;
  }

  // Handle 'dec' operations
  if (patch.dec) {
    operations.dec = patch.dec;
  }

  // Handle 'insert' operations (for arrays)
  if (patch.insert) {
    operations.insert = patch.insert;
  }

  // Handle 'diffMatchPatch' operations
  if (patch.diffMatchPatch) {
    operations.diffMatchPatch = patch.diffMatchPatch;
  }

  return operations;
}

// Export the refactored function
export { modifyDocuments };

import { sanityApi } from '../utils/sanityClient.js';

interface Project {
  id: string;
  displayName: string;
  studioHost?: string;
  externalStudioHost?: string;
  organizationId?: string;
}

interface Organization {
  organizationId: string;
  organizationName: string;
  projects: {
    id: string;
    displayName: string;
    studioHost?: string;
    externalStudioHost?: string;
  }[];
}

interface Studio {
  type: 'sanity-hosted' | 'external';
  url: string;
}

interface StudiosResult {
  studios: Studio[];
  message?: string;
}

/**
 * List all organizations and their projects that the user has access to
 * 
 * @returns Array of organizations with their projects
 */
export async function listOrganizationsAndProjects(): Promise<Organization[]> {
  try {
    // Fetch all projects
    const projects = await sanityApi.listProjects() as Project[];

    // Group projects by organization
    const orgMap = new Map<string | null, Organization>();

    // Add "Personal Projects" group for projects without organization
    orgMap.set(null, {
      organizationId: 'personal',
      organizationName: 'Personal Projects',
      projects: []
    });

    // Group projects by organization
    projects.forEach(project => {
      const orgId = project.organizationId || null;

      if (!orgMap.has(orgId) && orgId !== null) {
        orgMap.set(orgId, {
          organizationId: orgId,
          organizationName: `Organization ${orgId}`, // We don't have org names from the API
          projects: []
        });
      }

      // Add project to the organization
      const org = orgMap.get(orgId);
      if (org) {
        org.projects.push({
          id: project.id,
          displayName: project.displayName,
          studioHost: project.studioHost,
          externalStudioHost: project.externalStudioHost
        });
      }
    });

    // Convert map to array
    return Array.from(orgMap.values())
      // Remove empty organizations
      .filter(org => org.projects.length > 0);

  } catch (error: any) {
    console.error('Error listing organizations and projects:', error);
    throw new Error(`Failed to list organizations and projects: ${error.message}`);
  }
}

/**
 * List all studios for a specific project
 * 
 * @param projectId - Sanity project ID
 * @returns Array of studio URLs
 */
export async function listStudios(projectId: string): Promise<StudiosResult> {
  try {
    // Fetch all projects to find the one with matching ID
    const projects = await sanityApi.listProjects() as Project[];
    const project = projects.find(p => p.id === projectId);

    if (!project) {
      throw new Error(`Project not found: ${projectId}`);
    }

    const studios: Studio[] = [];

    // Add Sanity-hosted studio if available
    if (project.studioHost) {
      studios.push({
        type: 'sanity-hosted',
        url: `https://${project.studioHost}.sanity.studio/`
      });
    }

    // Add external studio if available
    if (project.externalStudioHost) {
      studios.push({
        type: 'external',
        url: project.externalStudioHost
      });
    }

    // If no studios found, include a note
    if (studios.length === 0) {
      return {
        studios: [],
        message: 'No hosted studio - Studio may be local only'
      };
    }

    return { studios };

  } catch (error: any) {
    console.error(`Error listing studios for project ${projectId}:`, error);
    throw new Error(`Failed to list studios: ${error.message}`);
  }
}

import { createSanityClient, sanityApi, isSufficientApiVersion, SanityClient, SanityDocument, SanityActionResult } from '../utils/sanityClient.js';
import { normalizeBaseDocId, normalizeDocumentIds, createErrorResponse, getDocumentContent } from '../utils/documentHelpers.js';
import config from '../config/config.js';
import { SanityError, ReleaseOptions, ReleaseDocument } from '../types/sanity.js';

// Minimum API version required for Content Releases
const REQUIRED_API_VERSION = '2024-05-23';

/**
 * Checks if the configured API version is sufficient for Content Releases
 * Throws an error if the version is too old
 */
function validateApiVersion(): void {
  // Skip validation for test environment (vi.mock will set this)
  if (process.env.NODE_ENV === 'test') {
    return;
  }

  const currentVersion = config.apiVersion;

  if (!isSufficientApiVersion(currentVersion, REQUIRED_API_VERSION)) {
    throw new Error(
      `API version ${currentVersion} is outdated. Please update to version ${REQUIRED_API_VERSION} or later to use Content Releases. You can do this by updating your SANITY_API_VERSION in .env or config.`
    );
  }
}

/**
 * Validates release creation options and parameters
 */
function validateReleaseParameters(
  releaseId: string,
  options?: ReleaseOptions
): void {
  // Validate that scheduled releases have intendedPublishAt
  if (options?.releaseType === 'scheduled' && !options?.intendedPublishAt) {
    throw new Error('publishAt is required for scheduled releases');
  }
}

/**
 * Creates release action metadata from parameters
 */
function createReleaseMetadata(
  releaseId: string,
  title?: string,
  options?: ReleaseOptions
): Record<string, string | undefined> {
  return {
    title: title || `Release: ${releaseId}`,
    ...(options?.description && { description: options.description }),
    ...(options?.releaseType && { releaseType: options.releaseType }),
    ...(options?.intendedPublishAt && { intendedPublishAt: options.intendedPublishAt })
  };
}

/**
 * Handles common release creation errors with descriptive messages
 */
function handleReleaseCreationError(error: SanityError, releaseId: string): never {
  console.error(`Error creating release ${releaseId}:`, error);

  // Check for common issues
  if (error.message?.includes('API version')) {
    throw new Error(`Failed to create release: Make sure you're using API version 2024-05-23 or later.`);
  } else if (error.statusCode === 404 || error.message?.includes('not found')) {
    throw new Error(`Failed to create release: The Content Releases feature might not be enabled for this project or the API token lacks permissions.`);
  } else if (error.statusCode === 401 || error.statusCode === 403 || error.message?.includes('Not authorized')) {
    throw new Error(`Failed to create release: Authentication failed. Check that your Sanity token has permission to create releases.`);
  } else {
    throw new Error(`Failed to create release: ${error.message}`);
  }
}

/**
 * Creates a new content release
 * 
 * @param projectId - Sanity project ID
 * @param dataset - Dataset name
 * @param releaseId - Unique ID for the release
 * @param title - Display title for the release
 * @param options - Optional metadata for the release
 * @returns Result of creating the release
 */
export async function createRelease(
  projectId: string, 
  dataset: string, 
  releaseId: string, 
  title?: string,
  options?: ReleaseOptions
): Promise<{
  success: boolean;
  message: string;
  releaseId: string;
  result: SanityActionResult;
}> {
  try {
    // Check API version first
    validateApiVersion();

    // Validate release parameters
    validateReleaseParameters(releaseId, options);

    // Create the release action
    const action = {
      actionType: 'sanity.action.release.create',
      releaseId,
      metadata: createReleaseMetadata(releaseId, title, options)
    };

    // Call the Actions API
    const result = await sanityApi.performActions(projectId, dataset, [action]);

    return {
      success: true,
      message: `Release ${releaseId} created successfully`,
      releaseId,
      result
    };
  } catch (error: any) {
    handleReleaseCreationError(error, releaseId);
  }
}

/**
 * Creates a version document action for a document in a release
 * 
 * @param releaseId - ID of the release
 * @param documentId - ID of the document to create a version for
 * @param attributes - Document content/attributes
 * @returns The created action object
 */
function createVersionAction(releaseId: string, documentId: string, attributes: SanityDocument) {
  const baseDocId = normalizeBaseDocId(documentId);
  const versionId = `versions.${releaseId}.${baseDocId}`;

  return {
    actionType: 'sanity.action.document.version.create',
    publishedId: baseDocId,
    attributes: {
      ...attributes,
      _id: versionId
    }
  };
}

/**
 * Processes a single document for adding to a release
 * 
 * @param client - Sanity client
 * @param releaseId - ID of the release
 * @param documentId - ID of the document to process
 * @param content - Optional custom content
 * @returns Object containing the processed document's action, ID, and version ID
 * @throws Error if document is not found
 */
async function processDocumentForRelease(
  client: SanityClient,
  releaseId: string,
  documentId: string,
  content?: SanityDocument
) {
  const baseDocId = normalizeBaseDocId(documentId);

  // Use provided content or fetch from Sanity
  const attributes = content || await getDocumentContent(client, documentId);

  // Create version ID
  const versionId = `versions.${releaseId}.${baseDocId}`;

  // Create the version action
  const action = createVersionAction(releaseId, documentId, attributes);

  return {
    action,
    documentId: baseDocId,
    versionId
  };
}

/**
 * Adds a document or multiple documents to a content release
 * 
 * @param projectId - Sanity project ID
 * @param dataset - Dataset name
 * @param releaseId - ID of the release
 * @param documentIds - ID or array of IDs of the document(s) to add to the release
 * @param content - Optional custom content to use for the document version
 * @returns Result of adding the document(s) to the release
 */
export async function addDocumentToRelease(
  projectId: string, 
  dataset: string, 
  releaseId: string, 
  documentIds: string | string[], 
  content?: SanityDocument
): Promise<{
  success: boolean;
  message: string;
  releaseId: string;
  documentIds: string[];
  versionIds: string[];
  result: SanityActionResult;
}> {
  try {
    // Check API version first
    validateApiVersion();

    // Normalize to always work with an array of document IDs
    const normalizedIds = normalizeDocumentIds(documentIds);

    if (normalizedIds.length === 0) {
      throw new Error('No valid document IDs provided');
    }

    // Create a Sanity client
    const client = createSanityClient(projectId, dataset, {
      apiVersion: config.apiVersion,
      token: config.sanityToken
    });

    // Process each document and collect actions
    const actions = [];
    const processedIds = [];
    const versionIds = [];
    const errors: string[] = [];

    for (const docId of normalizedIds) {
      try {
        const { action, documentId, versionId } = await processDocumentForRelease(client, releaseId, docId, content);
        actions.push(action);
        processedIds.push(documentId);
        versionIds.push(versionId);
      } catch (documentError: any) {
        console.error(`Error processing document ${docId}:`, documentError);
        errors.push(`Document ID ${docId}: ${documentError.message}`);
      }
    }

    // If no documents were processed successfully, throw an error with all collected error messages
    if (processedIds.length === 0) {
      throw new Error(`Failed to add any documents to release: ${errors.join('; ')}`);
    }

    // Call the Actions API with all document actions
    const result = await sanityApi.performActions(projectId, dataset, actions);

    // If we have both successful and failed documents, include that in the message
    let message = `${processedIds.length} document(s) added to release ${releaseId} successfully`;
    if (errors.length > 0) {
      message += `. Warning: ${errors.length} document(s) could not be added`;
      console.warn(`Some documents could not be added to release ${releaseId}:`, errors);
    }

    return {
      success: true,
      message,
      releaseId,
      documentIds: processedIds,
      versionIds,
      result
    };
  } catch (error: any) {
    throw createErrorResponse(`Failed to add document(s) to release ${releaseId}`, error);
  }
}

/**
 * Removes one or more documents from a content release
 * 
 * @param projectId - Sanity project ID
 * @param dataset - Dataset name
 * @param releaseId - ID of the release
 * @param documentIds - ID or array of IDs of the document(s) to remove from the release
 * @returns Result of removing the document(s) from the release
 */
export async function removeDocumentFromRelease(
  projectId: string, 
  dataset: string, 
  releaseId: string, 
  documentIds: string | string[]
): Promise<{
  success: boolean;
  message: string;
  releaseId: string;
  documentIds: string[];
  result: SanityActionResult;
}> {
  try {
    validateApiVersion();

    // Normalize to always work with an array of document IDs
    const normalizedIds = normalizeDocumentIds(documentIds);

    if (normalizedIds.length === 0) {
      throw new Error('No valid document IDs provided');
    }

    // Process each document ID for removal
    const actions = [];
    const processedIds = [];

    for (const docId of normalizedIds) {
      try {
        const baseDocId = normalizeBaseDocId(docId);
        const versionId = `versions.${releaseId}.${baseDocId}`;

        actions.push({
          actionType: 'sanity.action.document.delete',
          documentId: versionId
        });

        processedIds.push(baseDocId);
      } catch (documentError: any) {
        console.error(`Error processing document ${docId} for removal:`, documentError);
      }
    }

    // If no documents were successfully processed, throw an error
    if (actions.length === 0) {
      throw new Error('Failed to remove any documents from release');
    }

    // Execute the actions
    const result = await sanityApi.performActions(projectId, dataset, actions);

    return {
      success: true,
      message: `${processedIds.length} document(s) removed from release ${releaseId} successfully`,
      releaseId,
      documentIds: processedIds,
      result
    };
  } catch (error: any) {
    console.error(`Error removing document(s) from release ${releaseId}:`, error);
    throw createErrorResponse(`Failed to remove document(s) from release ${releaseId}: ${error.message}`, error);
  }
}

/**
 * Lists all documents in a content release
 * 
 * @param projectId - Sanity project ID
 * @param dataset - Dataset name
 * @param releaseId - ID of the release
 * @returns List of documents in the release
 */
interface ReleaseDocument {
  versionId: string;
  documentId: string;
  type: string;
  title: string;
}

export async function listReleaseDocuments(
  projectId: string, 
  dataset: string, 
  releaseId: string
): Promise<{
  releaseId: string;
  documentCount: number;
  documents: ReleaseDocument[];
}> {
  try {
    // Check API version first
    validateApiVersion();

    // Create client with perspective: 'raw' as required for sanity::partOfRelease
    const client = createSanityClient(projectId, dataset, { perspective: 'raw' });

    // Use the sanity::partOfRelease function to get all documents in the release
    const query = `*[sanity::partOfRelease($releaseId)]{ _id, _type, title }`;
    const params = { releaseId: releaseId };

    const documents = await client.fetch(query, params);

    // Map version documents to their base documents
    const mappedDocuments: ReleaseDocument[] = documents.map((doc: SanityDocument) => {
      // Extract the base document ID from the version ID
      const baseId = doc._id.replace(`versions.${releaseId}.`, '');

      return {
        versionId: doc._id,
        documentId: baseId,
        type: doc._type,
        title: doc.title || `Untitled ${doc._type}`,
      };
    });

    return {
      releaseId,
      documentCount: mappedDocuments.length,
      documents: mappedDocuments
    };
  } catch (error: any) {
    console.error(`Error listing documents in release ${releaseId}:`, error);
    throw new Error(`Failed to list release documents: ${error.message}`);
  }
}

/**
 * Publishes all documents in a release
 * 
 * @param projectId - Sanity project ID
 * @param dataset - Dataset name
 * @param releaseId - ID of the release to publish
 * @returns Result of publishing the release
 */
export async function publishRelease(
  projectId: string, 
  dataset: string, 
  releaseId: string
): Promise<{
  success: boolean;
  message: string;
  releaseId: string;
  documentCount: number;
  result: SanityActionResult;
}> {
  try {
    // Check API version first
    validateApiVersion();

    // First, check how many documents are in the release
    const documents = await listReleaseDocuments(projectId, dataset, releaseId);

    // Check if release exceeds document limit (50 docs per release as per docs)
    if (documents.documentCount > 50) {
      throw new Error(
        `Release contains ${documents.documentCount} documents, which exceeds the 50 document limit`
      );
    }

    // Create the publish release action
    const action = {
      actionType: 'sanity.action.release.publish',
      releaseId
    };

    // Call the Actions API
    const result = await sanityApi.performActions(projectId, dataset, [action]);

    return {
      success: true,
      message: `Release ${releaseId} published successfully`,
      releaseId,
      documentCount: documents.documentCount,
      result
    };
  } catch (error: any) {
    console.error(`Error publishing release ${releaseId}:`, error);
    throw new Error(`Failed to publish release: ${error.message}`);
  }
}

/**
 * Lists all releases for a project and dataset
 * 
 * @param projectId - Sanity project ID
 * @param dataset - Dataset name
 * @returns List of all releases
 */
export async function listReleases(
  projectId: string,
  dataset: string
): Promise<{
  releases: SanityDocument[];
}> {
  try {
    // Check API version first
    validateApiVersion();

    const client = createSanityClient(projectId, dataset);

    // Query for all releases using the GROQ function
    const query = `releases::all()`;
    const releases = await client.fetch(query);

    return {
      releases
    };
  } catch (error: any) {
    console.error(`Error listing releases:`, error);

    // Check for common issues
    if (error.message?.includes('Unknown GROQ function "releases::all"')) {
      throw new Error(`Failed to list releases: The releases::all() function is not available. Make sure you're using API version 2024-05-23 or later.`);
    } else if (error.statusCode === 404) {
      throw new Error(`Failed to list releases: The Content Releases feature might not be enabled for this project or the API token lacks permissions.`);
    } else if (error.statusCode === 401 || error.statusCode === 403) {
      throw new Error(`Failed to list releases: Authentication failed. Check that your Sanity token has permission to access releases.`);
    } else {
      throw new Error(`Failed to list releases: ${error.message}`);
    }
  }
}

/**
 * Gets a specific release by ID
 * 
 * @param projectId - Sanity project ID
 * @param dataset - Dataset name
 * @param releaseId - ID of the release to retrieve
 * @returns The release data
 */
export async function getRelease(
  projectId: string,
  dataset: string,
  releaseId: string
): Promise<{
  release: SanityDocument;
}> {
  try {
    // Check API version first
    validateApiVersion();

    const client = createSanityClient(projectId, dataset);

    // Query for the specific release
    const query = `*[_id == "_.releases.${releaseId}"]`;
    const releases = await client.fetch(query);

    if (!releases || releases.length === 0) {
      throw new Error(`Release with ID ${releaseId} not found`);
    }

    return {
      release: releases[0]
    };
  } catch (error: any) {
    console.error(`Error getting release ${releaseId}:`, error);
    throw new Error(`Failed to get release: ${error.message}`);
  }
}

/**
 * Updates a release's information
 * 
 * @param projectId - Sanity project ID
 * @param dataset - Dataset name
 * @param releaseId - ID of the release to update
 * @param updateData - Data to update on the release
 * @returns Result of updating the release
 */
export async function updateRelease(
  projectId: string,
  dataset: string,
  releaseId: string,
  updateData: {
    title?: string;
    description?: string;
    releaseType?: 'asap' | 'scheduled';
    intendedPublishAt?: string;
  }
): Promise<{
  success: boolean;
  message: string;
  releaseId: string;
  result: SanityActionResult;
}> {
  try {
    // Check API version first
    validateApiVersion();

    // Validate required fields for scheduled releases
    if (updateData.releaseType === 'scheduled' && !updateData.intendedPublishAt) {
      throw new Error('intendedPublishAt is required for scheduled releases');
    }

    // Create the metadata object with only provided fields
    const metadata: Record<string, any> = {};
    if (updateData.title) metadata.title = updateData.title;
    if (updateData.description) metadata.description = updateData.description;
    if (updateData.releaseType) metadata.releaseType = updateData.releaseType;
    if (updateData.intendedPublishAt) metadata.intendedPublishAt = updateData.intendedPublishAt;

    // Create the release edit action
    const action = {
      actionType: 'sanity.action.release.edit',
      releaseId,
      patch: {
        id: releaseId, // Add the id property required by SanityAction interface
        set: {
          metadata
        }
      }
    };

    // Call the Actions API
    const result = await sanityApi.performActions(projectId, dataset, [action]);

    return {
      success: true,
      message: `Release ${releaseId} updated successfully`,
      releaseId,
      result
    };
  } catch (error: any) {
    console.error(`Error updating release ${releaseId}:`, error);
    throw new Error(`Failed to update release: ${error.message}`);
  }
}

