/**
 * Document helper utility functions for Sanity operations
 */
import { SanityClient, SanityPatch, SanityDocument, SanityError, PatchOperations, InsertOperation } from '../types/sanity.js';

/**
 * Normalizes document ID to ensure it has a 'drafts.' prefix
 * 
 * @param documentId - The document ID to normalize
 * @returns The normalized document ID with 'drafts.' prefix
 */
export function normalizeDraftId(documentId: string): string {
  return documentId.startsWith('drafts.') ? documentId : `drafts.${documentId}`;
}

/**
 * Normalizes a document ID by removing any 'drafts.' prefix
 * 
 * @param documentId - The document ID to normalize
 * @returns The normalized document ID without 'drafts.' prefix
 */
export function normalizeBaseDocId(documentId: string): string {
  return documentId.replace(/^drafts\./, '');
}

/**
 * Applies patch operations to a Sanity patch object
 * 
 * @param patch - The patch operations to apply
 * @param patchObj - The Sanity patch object to modify
 */
export function applyPatchOperations(patch: Record<string, any>, patchObj: SanityPatch): void {
  if (patch.set) patchObj.set(patch.set);
  if (patch.setIfMissing) patchObj.setIfMissing(patch.setIfMissing);
  if (patch.unset) patchObj.unset(patch.unset);
  if (patch.inc) patchObj.inc(patch.inc);
  if (patch.dec) patchObj.dec(patch.dec);

  // Handle insert operations for arrays
  if (patch.insert) {
    const { items, position, at } = patch.insert;
    if (items && position && at) {
      patchObj.insert(position, at, items);
    }
  }

  // Handle diffMatchPatch operations for string patching
  if (patch.diffMatchPatch) {
    patchObj.diffMatchPatch(patch.diffMatchPatch);
  }
}

/**
 * Retrieves document content, trying draft first then published version
 * 
 * @param client - Sanity client
 * @param documentId - The document ID to retrieve
 * @param fallbackContent - Optional fallback content
 * @returns The document content or fallback content
 * @throws Error if document not found and no fallback content provided
 */
export async function getDocumentContent(
  client: SanityClient, 
  documentId: string, 
  fallbackContent?: SanityDocument
): Promise<SanityDocument> {
  const baseDocId = normalizeBaseDocId(documentId);
  const draftId = `drafts.${baseDocId}`;

  try {
    // First try to get the draft version
    let documentContent = await client.getDocument(draftId);

    // If draft not found, try the published version
    if (!documentContent) {
      documentContent = await client.getDocument(baseDocId);
    }

    // If content was found, return it
    if (documentContent) {
      return documentContent;
    }
  } catch (e) {
    // Intentionally empty, will fall through to check fallback content
  }

  // If content parameter is provided, use that instead
  if (fallbackContent) {
    return fallbackContent;
  }

  throw new Error(`Document ${baseDocId} not found`);
}

/**
 * Creates a standardized error response for controller functions
 * 
 * @param message - The error message
 * @param error - The original error object (optional)
 * @returns Standardized error message
 */
export function createErrorResponse(message: string, error?: Error | SanityError): Error {
  if (error) {
    console.error(`${message}:`, error);
    return new Error(`${message}: ${error.message}`);
  } else {
    console.error(message);
    return new Error(message);
  }
}

/**
 * Normalizes document IDs from various input formats
 * Handles single IDs, arrays, and JSON string representations
 * 
 * @param documentIds - Document IDs in various possible formats
 * @returns Normalized array of document IDs
 * @throws Error if no valid document IDs are provided
 */
export function normalizeDocumentIds(documentIds: string | string[]): string[] {
  let parsedDocIds: string[];

  if (Array.isArray(documentIds)) {
    // Already an array - use as is
    parsedDocIds = documentIds;
  } else if (typeof documentIds === 'string') {
    // Check if this is a JSON string array
    if (documentIds.startsWith('[') && documentIds.endsWith(']')) {
      try {
        // Attempt to parse as JSON
        const parsed = JSON.parse(documentIds);
        if (Array.isArray(parsed)) {
          // Successfully parsed as array
          parsedDocIds = parsed;
        } else {
          // Parsed as something else (object, number, etc.) - treat as single ID
          parsedDocIds = [documentIds];
        }
      } catch (e) {
        // If parsing fails, treat as a single string
        parsedDocIds = [documentIds];
      }
    } else {
      // Regular string - treat as single ID
      parsedDocIds = [documentIds];
    }
  } else {
    // Unexpected type - convert to string and use as single ID
    parsedDocIds = [String(documentIds)];
  }

  if (parsedDocIds.length === 0) {
    throw new Error("No document IDs provided");
  }

  return parsedDocIds;
}

import { marked } from 'marked';
import { gfmHeadingId } from 'marked-gfm-heading-id';

// Use the heading ID extension
marked.use(gfmHeadingId());

interface PortableTextSpan {
  _key?: string;
  _type?: string;
  text?: string;
  marks?: string[];
}

interface PortableTextBlock {
  _key?: string;
  _type: string;
  style?: string;
  listItem?: 'bullet' | 'number';
  children?: PortableTextSpan[];
  markDefs?: any[];
  level?: number;
  caption?: string;
  asset?: {
    _ref: string;
    [key: string]: any;
  };
  [key: string]: any;
}

/**
 * Converts Portable Text to Markdown
 * 
 * @param blocks - Array of Portable Text blocks
 * @returns Markdown string
 */
export function portableTextToMarkdown(blocks: PortableTextBlock[]): string {
  if (!blocks || !Array.isArray(blocks)) {
    return '';
  }

  return blocks.map(block => {
    // Handle different block types
    if (block._type === 'block') {
      // Convert text spans with marks to markdown
      const text = block.children
        ?.map(span => {
          let result = span.text || '';

          // Apply marks (bold, italic, etc)
          if (span.marks && span.marks.length > 0) {
            span.marks.forEach(mark => {
              // Handle different mark types
              if (mark === 'strong') {
                result = `**${result}**`;
              } else if (mark === 'em') {
                result = `*${result}*`;
              } else if (mark === 'code') {
                result = `\`${result}\``;
              }
              // Additional marks could be handled here
            });
          }

          return result;
        })
        .join('') || '';

      // Handle block styles (headings, lists, etc)
      if (block.style === 'h1') {
        return `# ${text}`;
      } else if (block.style === 'h2') {
        return `## ${text}`;
      } else if (block.style === 'h3') {
        return `### ${text}`;
      } else if (block.style === 'h4') {
        return `#### ${text}`;
      } else if (block.style === 'h5') {
        return `##### ${text}`;
      } else if (block.style === 'h6') {
        return `###### ${text}`;
      } else if (block.style === 'blockquote') {
        return `> ${text}`;
      } else if (block.listItem === 'bullet') {
        return `- ${text}`;
      } else if (block.listItem === 'number') {
        return `1. ${text}`;
      }

      // Default to normal paragraph
      return text;
    } else if (block._type === 'image' && block.asset) {
      // Handle image blocks
      const caption = block.caption || '';
      return `![${caption}](${block.asset._ref})`;
    }

    // Handle other block types or return empty string
    return '';
  }).join('\n\n');
}

/**
 * Converts Markdown to Portable Text
 * 
 * @param markdown - Markdown string to convert
 * @returns Array of Portable Text blocks
 */
export function markdownToPortableText(markdown: string): PortableTextBlock[] {
  if (!markdown) {
    return [];
  }

  // Parse markdown to tokens
  const tokens = marked.lexer(markdown);
  const blocks: PortableTextBlock[] = [];

  tokens.forEach(token => {
    // Handle different token types
    if (token.type === 'paragraph') {
      blocks.push({
        _key: generateKey(),
        _type: 'block',
        style: 'normal',
        children: [
          {
            _key: generateKey(),
            _type: 'span',
            text: token.text,
            marks: []
          }
        ],
        markDefs: []
      });
    } else if (token.type === 'heading') {
      blocks.push({
        _key: generateKey(),
        _type: 'block',
        style: `h${token.depth}`,
        children: [
          {
            _key: generateKey(),
            _type: 'span',
            text: token.text,
            marks: []
          }
        ],
        markDefs: []
      });
    } else if (token.type === 'blockquote') {
      blocks.push({
        _key: generateKey(),
        _type: 'block',
        style: 'blockquote',
        children: [
          {
            _key: generateKey(),
            _type: 'span',
            text: token.text,
            marks: []
          }
        ],
        markDefs: []
      });
    } else if (token.type === 'list') {
      token.items.forEach((item: any) => {
        blocks.push({
          _key: generateKey(),
          _type: 'block',
          style: 'normal',
          listItem: token.ordered ? 'number' : 'bullet',
          level: 1,
          children: [
            {
              _key: generateKey(),
              _type: 'span',
              text: item.text,
              marks: []
            }
          ],
          markDefs: []
        });
      });
    } else if (token.type === 'code') {
      blocks.push({
        _key: generateKey(),
        _type: 'block',
        style: 'normal',
        children: [
          {
            _key: generateKey(),
            _type: 'span',
            text: token.text,
            marks: ['code']
          }
        ],
        markDefs: []
      });
    } else if (token.type === 'image') {
      blocks.push({
        _key: generateKey(),
        _type: 'image',
        caption: token.text,
        asset: {
          _ref: token.href
        }
      });
    }
    // Additional token types can be handled here
  });

  return blocks;
}

/**
 * Generate a random key for Portable Text objects
 */
function generateKey(): string {
  return Math.random().toString(36).substring(2, 15);
}

import { createClient, SanityClient } from '@sanity/client';
import fetch from 'node-fetch';
import config from '../config/config.js';

/**
 * Creates a Sanity client for a specific project and dataset
 * 
 * @param projectId - Sanity project ID
 * @param dataset - Dataset name (default: 'production')
 * @param options - Additional options to pass to the Sanity client
 * @returns Configured Sanity client instance
 */
export function createSanityClient(
  projectId: string, 
  dataset: string = 'production', 
  options: Record<string, any> = {}
): SanityClient {
  return createClient({
    projectId,
    dataset,
    apiVersion: config.apiVersion,
    token: config.sanityToken,
    useCdn: false, // Using the API directly ensures we get fresh content
    ...options
  });
}

interface SanityProject {
  id: string;
  displayName: string;
  organizationId: string;
  isDefault: boolean;
  createdAt: string;
  studioHost?: string;
  members: Array<{
    id: string;
    role: string;
  }>;
}

interface SanityAction {
  create?: Record<string, any>;
  createOrReplace?: Record<string, any>;
  createIfNotExists?: Record<string, any>;
  patch?: {
    id: string;
    set?: Record<string, any>;
    setIfMissing?: Record<string, any>;
    unset?: string[];
    insert?: Record<string, any>;
    inc?: Record<string, any>;
    dec?: Record<string, any>;
  };
  delete?: {
    id: string;
  };
  // Custom Sanity actions
  actionType?: string;
  draftId?: string;
  publishedId?: string;
  releaseId?: string;
  metadata?: Record<string, any>;
  attributes?: Record<string, any>;
}

interface SanityActionResult {
  transactionId: string;
  results: Array<{
    id: string;
    document?: Record<string, any>;
  }>;
}

/**
 * Checks if the provided API version is sufficient for the required minimum version
 * 
 * @param currentVersion - Current API version (e.g., '2024-05-23')
 * @param requiredVersion - Minimum required API version
 * @returns True if current version is equal to or newer than required
 */
export function isSufficientApiVersion(currentVersion: string, requiredVersion: string): boolean {
  // Convert versions to comparable format (YYYY-MM-DD  YYYYMMDD)
  const formatVersion = (version: string): number => {
    // Remove 'v' prefix if present
    const cleanVersion = version.replace(/^v/, '');

    // Handle versions without dashes (already in YYYYMMDD format)
    if (!cleanVersion.includes('-')) {
      // Make sure it's a valid 8-digit number
      const numVersion = parseInt(cleanVersion, 10);
      if (!isNaN(numVersion) && cleanVersion.length === 8) {
        return numVersion;
      }
    }

    // Convert from YYYY-MM-DD format
    const parts = cleanVersion.split('-');
    if (parts.length !== 3) {
      throw new Error(`Invalid version format: ${version}. Expected YYYY-MM-DD`);
    }

    return parseInt(parts.join(''), 10);
  };

  try {
    const current = formatVersion(currentVersion);
    const required = formatVersion(requiredVersion);

    return current >= required;
  } catch (error) {
    console.error(`Error comparing API versions: ${error}`);
    // If there's any error in parsing, assume version is insufficient
    return false;
  }
}

/**
 * Makes direct HTTP requests to Sanity APIs not covered by the client
 */
export const sanityApi = {
  /**
   * Fetches all projects accessible to the user
   * 
   * @returns Promise with list of projects
   */
  async listProjects(): Promise<SanityProject[]> {
    const response = await fetch('https://api.sanity.io/v2021-06-07/projects', {
      headers: {
        'Authorization': `Bearer ${config.sanityToken}`
      }
    });

    if (!response.ok) {
      throw new Error(`Failed to fetch projects: ${response.statusText}`);
    }

    return response.json() as Promise<SanityProject[]>;
  },

  /**
   * Call the Sanity Actions API
   * 
   * @param projectId - Sanity project ID
   * @param dataset - Dataset name
   * @param actions - Array of action objects
   * @returns Promise with action result
   */
  async performActions(projectId: string, dataset: string, actions: SanityAction[]): Promise<SanityActionResult> {
    // IMPORTANT: The Actions API requires at least API version 2024-05-23
    // The correct endpoint is: https://{projectId}.api.sanity.io/v{apiVersion}/data/actions/{dataset}
    // Note: "actions" (plural) not "action" (singular)
    const url = `https://${projectId}.api.sanity.io/v${config.apiVersion}/data/actions/${dataset}`;

    console.log(`Calling actions API: ${url}`);

    const response = await fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${config.sanityToken}`
      },
      body: JSON.stringify({ actions })
    });

    if (!response.ok) {
      const errorText = await response.text();
      console.error(`Action API error response:`, {
        status: response.status,
        statusText: response.statusText,
        errorText,
        url
      });
      throw new Error(`Action API error: ${response.status} ${response.statusText} - ${errorText}`);
    }

    return response.json() as Promise<SanityActionResult>;
  }
};

