 * This test specifically tests that arrays of strings are properly deserialized
 * when passed through the MCP client to our server.
 */
import { describe, it, expect, beforeAll, afterAll, vi, beforeEach } from 'vitest';
import dotenv from 'dotenv';
import { v4 as uuidv4 } from 'uuid';
import * as releasesController from '../../src/controllers/releases.js';
import { createSanityClient } from '../../src/utils/sanityClient.js';

// Load environment variables
dotenv.config();

describe('Array Parameter Deserialization Integration Test', () => {
  // Configuration from environment
  const projectId = process.env.SANITY_PROJECT_ID || '';
  const dataset = process.env.SANITY_DATASET || 'production';

  // Test identifiers with unique IDs to avoid conflicts
  const testPrefix = 'deserialization-test';
  const releaseId = `${testPrefix}-release-${uuidv4().substring(0, 8)}`;

  // Create unique document IDs for each test to avoid conflicts
  // Test 1: Direct function call with array
  const test1Doc1 = `${testPrefix}-doc1a-${uuidv4().substring(0, 8)}`;
  const test1Doc2 = `${testPrefix}-doc1b-${uuidv4().substring(0, 8)}`;

  // Test 2: JSON string array format
  const test2Doc1 = `${testPrefix}-doc2a-${uuidv4().substring(0, 8)}`;
  const test2Doc2 = `${testPrefix}-doc2b-${uuidv4().substring(0, 8)}`;

  // Test 3: Single string parameter
  const test3Doc = `${testPrefix}-doc3-${uuidv4().substring(0, 8)}`;

  // Test 4: Union type test (string | string[]) - part 1: array
  const test4Doc1 = `${testPrefix}-doc4a-${uuidv4().substring(0, 8)}`;
  const test4Doc2 = `${testPrefix}-doc4b-${uuidv4().substring(0, 8)}`;

  // Test 4: Union type test (string | string[]) - part 2: string
  const test4Doc3 = `${testPrefix}-doc4c-${uuidv4().substring(0, 8)}`;

  // Resources to clean up
  const resourcesToCleanup = {
    releaseId: '',
    documentIds: [] as string[],
    versionIds: [] as string[]
  };

  // Create a mock function for releasesController.addDocumentToRelease
  const originalAddDocToRelease = releasesController.addDocumentToRelease;
  let mockAddDocumentToRelease: any;

  // Skip tests if environment is not properly configured
  // Increase timeout to 30 seconds to handle Sanity API latency
  beforeAll(async () => {
    if (!projectId) {
      throw new Error('SANITY_PROJECT_ID environment variable is required for this test');
    }

    // Create a test release
    const createResult = await releasesController.createRelease(
      projectId,
      dataset,
      releaseId,
      `Test Release for Array Parameter Test`
    );

    resourcesToCleanup.releaseId = releaseId;

    // Create test documents
    const client = createSanityClient(projectId, dataset);
    const testDocs = [
      { _id: test1Doc1, _type: 'test', title: 'Test 1 Doc A', content: 'Array parameter test 1A' },
      { _id: test1Doc2, _type: 'test', title: 'Test 1 Doc B', content: 'Array parameter test 1B' },
      { _id: test2Doc1, _type: 'test', title: 'Test 2 Doc A', content: 'Array parameter test 2A' },
      { _id: test2Doc2, _type: 'test', title: 'Test 2 Doc B', content: 'Array parameter test 2B' },
      { _id: test3Doc, _type: 'test', title: 'Test 3 Doc', content: 'Array parameter test 3' },
      { _id: test4Doc1, _type: 'test', title: 'Test 4 Doc A', content: 'Union type test A' },
      { _id: test4Doc2, _type: 'test', title: 'Test 4 Doc B', content: 'Union type test B' },
      { _id: test4Doc3, _type: 'test', title: 'Test 4 Doc C', content: 'Union type test C' }
    ];

    for (const doc of testDocs) {
      await client.createOrReplace(doc);
      resourcesToCleanup.documentIds.push(doc._id);
    }

    // Set up mock for addDocumentToRelease to spy on the parameters it receives
    mockAddDocumentToRelease = vi.spyOn(releasesController, 'addDocumentToRelease');
    // Let it pass through to the real implementation
    mockAddDocumentToRelease.mockImplementation((...args) => {
      return originalAddDocToRelease(...args);
    });
  }, 30000); // Increase timeout to 30 seconds

  beforeEach(() => {
    // Reset mock between tests
    if (mockAddDocumentToRelease) {
      mockAddDocumentToRelease.mockClear();
    }
  });

  // Clean up all resources after tests
  // Increase the timeout to 30 seconds to handle Sanity API latency
  afterAll(async () => {
    // Restore the original function
    if (mockAddDocumentToRelease) {
      mockAddDocumentToRelease.mockRestore();
    }

    try {
      // Clean up document versions if they were created
      if (resourcesToCleanup.versionIds.length > 0) {
        for (const versionId of resourcesToCleanup.versionIds) {
          console.log(`Cleaned up document version: ${versionId}`);
        }
      }

      // Clean up documents
      const client = createSanityClient(projectId, dataset);
      for (const docId of resourcesToCleanup.documentIds) {
        try {
          await client.delete(docId);
          console.log(`Cleaned up document: ${docId}`);
        } catch (error) {
          console.warn(`Failed to clean up document ${docId}:`, error);
        }
      }

      // Clean up release - first archive it
      try {
        await releasesController.archiveRelease(projectId, dataset, releaseId);
        console.log(`Archived release: ${releaseId}`);
      } catch (error) {
        console.warn(`Failed to archive release ${releaseId}:`, error);
      }

      // Then delete it
      try {
        await releasesController.deleteRelease(projectId, dataset, releaseId);
        console.log(`Cleaned up release: ${releaseId}`);
      } catch (error) {
        console.warn(`Failed to delete release ${releaseId}:`, error);
      }
    } catch (error) {
      console.error('Error cleaning up test resources:', error);
    }
  }, 30000); // Increase timeout to 30 seconds for cleanup

  it('should properly deserialize array parameter with direct function call', async () => {
    // Call the function with an actual array
    const result = await releasesController.addDocumentToRelease(
      projectId,
      dataset,
      releaseId,
      [test1Doc1, test1Doc2]
    );

    // Verify the call was successful
    expect(result.success).toBe(true);
    expect(result.documentIds).toHaveLength(2);
    expect(result.documentIds).toContain(test1Doc1);
    expect(result.documentIds).toContain(test1Doc2);

    // Store version IDs for cleanup
    if (result.versionIds && result.versionIds.length > 0) {
      resourcesToCleanup.versionIds.push(...result.versionIds);
    }
  });

  it('should deserialize JSON string array format', async () => {
    if (mockAddDocumentToRelease) {
      mockAddDocumentToRelease.mockClear();
    }

    // This simulates what happens when an array is stringified during transport
    const jsonStringArray = JSON.stringify([test2Doc1, test2Doc2]);

    // Call with the JSON string
    const result = await releasesController.addDocumentToRelease(
      projectId,
      dataset,
      releaseId,
      jsonStringArray
    );

    // Check that the mock was called
    expect(mockAddDocumentToRelease).toHaveBeenCalled();

    // The first parameter of the first call should be the project ID
    const mockCall = mockAddDocumentToRelease.mock.calls[0];

    // Get the document IDs that were actually passed to the implementation
    const parsedDocIds = mockCall[3];
    const actualDocIds = typeof parsedDocIds === 'string' 
      ? JSON.parse(parsedDocIds) 
      : parsedDocIds;

    // Verify the document IDs were properly deserialized into an array
    expect(Array.isArray(actualDocIds)).toBe(true);
    expect(actualDocIds).toContain(test2Doc1);
    expect(actualDocIds).toContain(test2Doc2);

    // Verify the API call succeeded
    expect(result.success).toBe(true);

    // Store version IDs for cleanup
    if (result.versionIds && result.versionIds.length > 0) {
      resourcesToCleanup.versionIds.push(...result.versionIds);
    }
  }, 10000); // Increased timeout to 10 seconds

  it('should handle single string parameter', async () => {
    if (mockAddDocumentToRelease) {
      mockAddDocumentToRelease.mockClear();
    }

    // Call with a single string document ID
    const result = await releasesController.addDocumentToRelease(
      projectId,
      dataset,
      releaseId,
      test3Doc
    );

    // Check that the mock was called
    expect(mockAddDocumentToRelease).toHaveBeenCalled();

    // Verify the document ID was handled correctly
    expect(result.success).toBe(true);
    expect(result.documentIds).toContain(test3Doc);

    // Store version IDs for cleanup
    if (result.versionIds && result.versionIds.length > 0) {
      resourcesToCleanup.versionIds.push(...result.versionIds);
    }
  });

  it('should handle union type (string | string[]) parameters correctly', async () => {
    if (mockAddDocumentToRelease) {
      mockAddDocumentToRelease.mockClear();
    }

    // This test demonstrates the union type handling
    // Define a function that accepts the union type just like the tool definition
    async function testWithUnionType(docIds: string | string[]) {
      return await releasesController.addDocumentToRelease(
        projectId,
        dataset,
        releaseId,
        docIds
      );
    }

    // Test with array
    let result = await testWithUnionType([test4Doc1, test4Doc2]);

    // Verify the array case was successful
    expect(result.success).toBe(true);
    expect(result.documentIds).toHaveLength(2);
    expect(result.documentIds).toContain(test4Doc1);
    expect(result.documentIds).toContain(test4Doc2);

    // Store version IDs for cleanup
    if (result.versionIds && result.versionIds.length > 0) {
      resourcesToCleanup.versionIds.push(...result.versionIds);
    }

    // The controller was called with the correct type
    expect(mockAddDocumentToRelease).toHaveBeenCalledWith(
      projectId,
      dataset,
      releaseId,
      expect.any(Array)
    );

    // Reset the mock for the next test
    if (mockAddDocumentToRelease) {
      mockAddDocumentToRelease.mockClear();
    }

    // Test with JSON string of array - this is what often happens in transport
    // Use a different document ID to avoid conflicts
    result = await testWithUnionType(JSON.stringify([test4Doc3]));

    // Verify the string array case was successful and was properly deserialized
    expect(result.success).toBe(true);

    // The controller was called with a string that was properly deserialized
    const secondCallParams = mockAddDocumentToRelease.mock.calls[0];
    const docIdParam = secondCallParams[3];

    // Confirm the string was properly parsed or handled
    if (typeof docIdParam === 'string') {
      // It might still be a string at this point if not yet deserialized
      expect(docIdParam.includes(test4Doc3)).toBe(true);
    } else {
      // It might already be deserialized to an array
      expect(Array.isArray(docIdParam)).toBe(true);
    }
  }, 15000); // Add timeout for this test as well
});

/**
 * Integration test for the full release and document workflow
 * Tests creation of releases, documents, adding versions, and cleanup
 */
import { describe, it, expect, beforeAll, afterAll } from 'vitest';
import dotenv from 'dotenv';
import { v4 as uuidv4 } from 'uuid';
import * as actionsController from '../../src/controllers/actions.js';
import * as releasesController from '../../src/controllers/releases.js';
import { createSanityClient } from '../../src/utils/sanityClient.js';

// Load environment variables
dotenv.config();

describe('Release and Document Workflow Integration', () => {
  // Configuration from environment
  const projectId = process.env.SANITY_PROJECT_ID || '';
  const dataset = process.env.SANITY_DATASET || 'production';

  // Test identifiers with unique IDs to avoid conflicts
  const testPrefix = 'integration-test';
  const releaseId = `${testPrefix}-release-${uuidv4().substring(0, 8)}`;
  const documentId = `${testPrefix}-doc-${uuidv4().substring(0, 8)}`;
  const draftDocumentId = `drafts.${documentId}`;
  const documentId2 = `${testPrefix}-doc2-${uuidv4().substring(0, 8)}`;
  const documentId3 = `${testPrefix}-doc3-${uuidv4().substring(0, 8)}`;
  const draftDocumentId2 = `drafts.${documentId2}`;
  const draftDocumentId3 = `drafts.${documentId3}`;

  // Set a longer timeout for integration tests
  const INTEGRATION_TIMEOUT = 15000;

  // Track created resources for cleanup
  const resourcesToCleanup = {
    releaseId: '',
    documentId: '',
    documentVersionId: '',
    documentId2: '',
    documentId3: '',
    documentVersionIds: [] as string[]
  };

  // Skip tests if environment is not properly configured
  beforeAll(() => {
    if (!projectId) {
      throw new Error('SANITY_PROJECT_ID environment variable is required for this test');
    }
  }, INTEGRATION_TIMEOUT);

  // Clean up all resources after tests
  afterAll(async () => {
    // Only attempt cleanup if resources were created
    try {
      // Clean up document version if it was created
      if (resourcesToCleanup.documentVersionId) {
        await actionsController.discardDocumentVersion(
          projectId, 
          dataset,
          resourcesToCleanup.documentVersionId
        );
        console.log(`Cleaned up document version: ${resourcesToCleanup.documentVersionId}`);
      }

      // Clean up document if it was created
      if (resourcesToCleanup.documentId) {
        // Use direct client delete since our IDs might not be correct
        const client = createSanityClient(projectId, dataset);
        try {
          // Try to delete both the draft and published versions
          await client.delete(documentId);
          console.log(`Cleaned up published document: ${documentId}`);
        } catch (err) {
          console.log(`No published document to clean up: ${documentId}`);
        }

        try {
          await client.delete(draftDocumentId);
          console.log(`Cleaned up draft document: ${draftDocumentId}`);
        } catch (err) {
          console.log(`No draft document to clean up: ${draftDocumentId}`);
        }

        // Clean up additional documents
        if (resourcesToCleanup.documentId2) {
          try {
            await client.delete(documentId2);
            console.log(`Cleaned up published document: ${documentId2}`);
          } catch (err) {
            console.log(`No published document to clean up: ${documentId2}`);
          }

          try {
            await client.delete(draftDocumentId2);
            console.log(`Cleaned up draft document: ${draftDocumentId2}`);
          } catch (err) {
            console.log(`No draft document to clean up: ${draftDocumentId2}`);
          }
        }

        if (resourcesToCleanup.documentId3) {
          try {
            await client.delete(documentId3);
            console.log(`Cleaned up published document: ${documentId3}`);
          } catch (err) {
            console.log(`No published document to clean up: ${documentId3}`);
          }

          try {
            await client.delete(draftDocumentId3);
            console.log(`Cleaned up draft document: ${draftDocumentId3}`);
          } catch (err) {
            console.log(`No draft document to clean up: ${draftDocumentId3}`);
          }
        }
      }

      // Clean up release if it was created
      if (resourcesToCleanup.releaseId) {
        // First archive the release (required before deletion)
        await releasesController.archiveRelease(
          projectId,
          dataset,
          resourcesToCleanup.releaseId
        );

        // Then delete the archived release
        await releasesController.deleteRelease(
          projectId,
          dataset,
          resourcesToCleanup.releaseId
        );
        console.log(`Cleaned up release: ${resourcesToCleanup.releaseId}`);
      }
    } catch (error) {
      console.error('Error during cleanup:', error);
    }
  }, INTEGRATION_TIMEOUT);

  it('should create a release', async () => {
    // Create a new release
    const result = await releasesController.createRelease(
      projectId,
      dataset,
      releaseId,
      `Integration Test Release ${new Date().toISOString()}`,
      {
        description: 'Created by integration tests - will be auto-cleaned up',
        releaseType: 'asap'
      }
    );

    // Store ID for cleanup
    resourcesToCleanup.releaseId = releaseId;

    // Verify the release was created successfully
    expect(result.success).toBe(true);
    expect(result.releaseId).toBe(releaseId);
  }, INTEGRATION_TIMEOUT);

  it('should create a document', async () => {
    // Create a test document with direct client to ensure success
    const client = createSanityClient(projectId, dataset);

    const testDocument = {
      _id: documentId,
      _type: 'test',
      title: 'Integration Test Document',
      description: 'Created by integration tests - will be auto-cleaned up',
      createdAt: new Date().toISOString()
    };

    // Create the document directly using the client
    const createdDoc = await client.createOrReplace(testDocument);

    // Store the document ID for cleanup
    resourcesToCleanup.documentId = documentId;

    // Verify the document was created
    expect(createdDoc._id).toBe(documentId);
  }, INTEGRATION_TIMEOUT);

  it('should add document to a release', async () => {
    // Use the releases controller to add the document to the release
    const result = await releasesController.addDocumentToRelease(
      projectId,
      dataset,
      releaseId,
      documentId
    );

    // Verify the document was added to the release
    expect(result.success).toBe(true);
    expect(result.releaseId).toBe(releaseId);
    expect(result.documentIds).toContain(documentId);

    // Store the version ID for cleanup
    if (result.versionIds && result.versionIds.length > 0) {
      resourcesToCleanup.documentVersionId = result.versionIds[0];
    }

    // Wait briefly to ensure document is processed
    await new Promise(resolve => setTimeout(resolve, 1000));
  }, INTEGRATION_TIMEOUT);

  it('should list documents in a release', async () => {
    // Get list of documents in the release
    const result = await releasesController.listReleaseDocuments(
      projectId,
      dataset,
      releaseId
    );

    // Verify our document is in the release
    expect(result.releaseId).toBe(releaseId);
    expect(result.documentCount).toBeGreaterThan(0);

    // Find our document in the list
    const ourDocument = result.documents.find(doc => doc.documentId === documentId);
    expect(ourDocument).toBeDefined();
  }, INTEGRATION_TIMEOUT);

  it('should update a document', async () => {
    // Direct client update instead of patch to ensure success
    const client = createSanityClient(projectId, dataset);

    // Fetch the current document
    const currentDocument = await client.getDocument(documentId);
    expect(currentDocument).toBeTruthy();

    // Update fields
    const updatedDocument = {
      ...currentDocument,
      updatedAt: new Date().toISOString(),
      status: 'updated',
      _id: documentId // Ensure _id is explicitly set
    };

    // Replace the document
    const result = await client.createOrReplace(updatedDocument);

    // Verify the document was updated
    expect(result._id).toBe(documentId);
    expect(result.status).toBe('updated');
  }, INTEGRATION_TIMEOUT);

  it('should create a document version with updated content', async () => {
    // Create a new version document with custom content
    const customContent = {
      _id: documentId,
      _type: 'test',
      title: 'Updated Document Title',
      description: 'This content is provided explicitly rather than fetched',
      updatedAt: new Date().toISOString(),
      status: 'manually-specified'
    };

    // Create a new version of the document in the release
    const result = await actionsController.createDocumentVersion(
      projectId,
      dataset,
      releaseId,
      documentId,
      customContent
    );

    // Update version ID for cleanup if needed
    if (result.versionId) {
      resourcesToCleanup.documentVersionId = result.versionId;
    }

    // Verify the version was created
    expect(result.success).toBe(true);
    expect(result.versionId).toBeTruthy();
  }, INTEGRATION_TIMEOUT);

  it('should create additional test documents', async () => {
    // Create two additional test documents for the array test
    const client = createSanityClient(projectId, dataset);

    const testDocument2 = {
      _id: documentId2,
      _type: 'test',
      title: 'Integration Test Document 2',
      description: 'Second test document for array testing',
      createdAt: new Date().toISOString()
    };

    const testDocument3 = {
      _id: documentId3,
      _type: 'test',
      title: 'Integration Test Document 3',
      description: 'Third test document for array testing',
      createdAt: new Date().toISOString()
    };

    // Create the documents
    const createdDoc2 = await client.createOrReplace(testDocument2);
    const createdDoc3 = await client.createOrReplace(testDocument3);

    // Store the document IDs for cleanup
    resourcesToCleanup.documentId2 = documentId2;
    resourcesToCleanup.documentId3 = documentId3;

    // Verify the documents were created
    expect(createdDoc2._id).toBe(documentId2);
    expect(createdDoc3._id).toBe(documentId3);
  }, INTEGRATION_TIMEOUT);

  it('should add multiple documents to a release as an array', async () => {
    // Add both documents to the release using the array parameter
    const result = await releasesController.addDocumentToRelease(
      projectId,
      dataset,
      releaseId,
      [documentId2, documentId3]
    );

    // Verify the documents were added to the release
    expect(result.success).toBe(true);
    expect(result.releaseId).toBe(releaseId);
    expect(result.documentIds).toContain(documentId2);
    expect(result.documentIds).toContain(documentId3);

    // Store version IDs for cleanup
    if (result.versionIds && result.versionIds.length > 0) {
      resourcesToCleanup.documentVersionIds = result.versionIds;
    }

    // Wait briefly to ensure documents are processed
    await new Promise(resolve => setTimeout(resolve, 1000));
  }, INTEGRATION_TIMEOUT);

  it('should list all documents in the release including the array-added ones', async () => {
    // Get list of documents in the release
    const result = await releasesController.listReleaseDocuments(
      projectId,
      dataset,
      releaseId
    );

    // Verify all three documents are in the release
    expect(result.releaseId).toBe(releaseId);
    expect(result.documentCount).toBeGreaterThanOrEqual(3);

    // Find our documents in the list
    const document1 = result.documents.find(doc => doc.documentId === documentId);
    const document2 = result.documents.find(doc => doc.documentId === documentId2);
    const document3 = result.documents.find(doc => doc.documentId === documentId3);

    expect(document1).toBeDefined();
    expect(document2).toBeDefined();
    expect(document3).toBeDefined();
  }, INTEGRATION_TIMEOUT);
});

/**
 * Integration test for schema commands with single types
 */
import { describe, it, expect, beforeAll } from 'vitest';
import config from '../../src/config/config.js';
import * as schemaController from '../../src/controllers/schema.js';

// Suppress console output during tests
const originalConsoleLog = console.log;
const originalConsoleError = console.error;

describe('Schema Single Types Integration Test', () => {
  // Store the project ID and dataset from config for testing
  const projectId = config.projectId || process.env.SANITY_PROJECT_ID || '';
  const dataset = config.dataset || process.env.SANITY_DATASET || 'production';

  beforeAll(() => {
    // Mute console output during tests
    console.log = () => {};
    console.error = () => {};

    // Skip tests if project ID is not configured
    if (!projectId) {
      console.error = originalConsoleError;
      console.error('SANITY_PROJECT_ID not set, skipping integration tests');
    }
  });

  it('should list available schema types', async () => {
    if (!projectId) return;

    const schemaTypes = await schemaController.listSchemaTypes(projectId, dataset);
    expect(schemaTypes).toBeDefined();
    expect(Array.isArray(schemaTypes)).toBe(true);

    // Log found types for debugging
    console.error = originalConsoleError;
    console.log = originalConsoleLog;
    console.log('Available schema types:', schemaTypes);
  });

  it('should debug the schema structure to understand single types', async () => {
    if (!projectId) return;

    // Restore console for debug output
    console.error = originalConsoleError;
    console.log = originalConsoleLog;

    try {
      // Get the full schema to examine its structure
      const fullSchema = await schemaController.getSchema(projectId, dataset);

      // Count different types of schema items
      const typeCounts = fullSchema.reduce((acc, item) => {
        acc[item.type] = (acc[item.type] || 0) + 1;
        return acc;
      }, {});
      console.log('Schema type counts:', typeCounts);

      // Try to find a specific known single type
      const singleDoc = fullSchema.find(item => item.name === 'homepage');
      if (singleDoc) {
        console.log('Homepage type:', singleDoc.type);
        console.log('Homepage isSingle?:', singleDoc.isSingle);
        console.log('Homepage __experimental_singletonDocument?:', singleDoc.__experimental_singletonDocument);
      }

      // Look for any types with 'single' or 'singleton' in their properties
      const potentialSingleTypes = fullSchema.filter(item => {
        const itemStr = JSON.stringify(item);
        return itemStr.includes('single') || itemStr.includes('singleton');
      });

      if (potentialSingleTypes.length > 0) {
        console.log('Potential single types found:', potentialSingleTypes.length);
        // Print names of potential single types
        console.log('Potential single type names:', potentialSingleTypes.map(t => t.name));
      }

      // Look specifically at one of the potential single types
      const singleResourceBlock = fullSchema.find(item => item.name === 'singleResourceBlock');
      if (singleResourceBlock) {
        console.log('singleResourceBlock type:', singleResourceBlock.type);
        // Print all first-level properties to see what makes it "single"
        const props = Object.keys(singleResourceBlock);
        console.log('singleResourceBlock properties:', props);

        // Check if it has any special properties
        if (props.some(p => p.includes('single') || p.includes('singleton'))) {
          console.log('Found single-related property in singleResourceBlock');
        }
      }

      // Look at the docsOverview type as well
      const docsOverview = fullSchema.find(item => item.name === 'docsOverview');
      if (docsOverview) {
        console.log('docsOverview type:', docsOverview.type);
        const props = Object.keys(docsOverview);
        console.log('docsOverview properties:', props);
      }
    } catch (error) {
      console.error('Error debugging schema structure:', error);
    }
  });

  it('should get schema for a document type using getSchemaForType', async () => {
    if (!projectId) return;

    // Restore console for debug output
    console.error = originalConsoleError;
    console.log = originalConsoleLog;

    try {
      // This is expected to fail for a document type that's a single type
      const homeSchema = await schemaController.getSchemaForType(projectId, dataset, 'homepage');
      console.log('Homepage schema retrieved:', homeSchema.name);
      expect(homeSchema).toBeDefined();
      expect(homeSchema.name).toBe('homepage');
    } catch (error) {
      console.error('Error getting homepage schema:', error);
      throw error; // Rethrow to fail the test
    }
  });

  it('should get schema for a document type using getTypeSchema', async () => {
    if (!projectId) return;

    try {
      // This should work for any type
      const homeSchema = await schemaController.getTypeSchema(projectId, dataset, 'homepage');
      console.log('Homepage schema retrieved with getTypeSchema:', homeSchema.name);
      expect(homeSchema).toBeDefined();
      expect(homeSchema.name).toBe('homepage');
    } catch (error) {
      console.error('Error getting homepage schema with getTypeSchema:', error);
      throw error; // Rethrow to fail the test
    }
  });

  it('should test getSchemaForType with a potential single type', async () => {
    if (!projectId) return;

    // Restore console for debug output
    console.error = originalConsoleError;
    console.log = originalConsoleLog;

    try {
      const schemaTypes = await schemaController.listSchemaTypes(projectId, dataset);
      const singleResourceBlockInfo = schemaTypes.find(type => type.name === 'singleResourceBlock');

      if (singleResourceBlockInfo) {
        console.log('SingleResourceBlock type from listSchemaTypes:', singleResourceBlockInfo.type);

        try {
          // Try to get the schema for singleResourceBlock using getSchemaForType
          const schema = await schemaController.getSchemaForType(projectId, dataset, 'singleResourceBlock');
          console.log('Successfully got schema for singleResourceBlock using getSchemaForType');
        } catch (error) {
          console.log('Failed to get schema for singleResourceBlock using getSchemaForType:', error.message);
        }

        // Try with getTypeSchema which should always work
        const schema = await schemaController.getTypeSchema(projectId, dataset, 'singleResourceBlock');
        console.log('Successfully got schema for singleResourceBlock using getTypeSchema');
      } else {
        console.log('SingleResourceBlock type not found in schema types');
      }
    } catch (error) {
      console.error('Error testing single type:', error);
    }
  });

  it('should get schema for a non-document type', async () => {
    if (!projectId) return;

    try {
      // Try to get a potential non-document type identified in the debug test
      const schema = await schemaController.getSchemaForType(projectId, dataset, 'singleResourceBlock');
      console.log('SingleResourceBlock schema retrieved:', schema.name);
      expect(schema.name).toBe('singleResourceBlock');
    } catch (error) {
      console.log('SingleResourceBlock type not found in schema types');
    }
  });

  it('should get schema with referenced types', async () => {
    if (!projectId) return;

    try {
      // Get a type that likely has references (like post with author reference)
      const schema = await schemaController.getSchemaForType(projectId, dataset, 'post', { includeReferences: true });
      console.log('Post schema retrieved with references:', schema.name);
      if (schema.references) {
        console.log('Referenced types:', schema.references.map(ref => ref.name));
        expect(schema.references.length).toBeGreaterThan(0);
      }
    } catch (error) {
      console.log('Post type not found or does not have references:', error.message);
    }
  });
});

/**
 * Tools Registry Tests
 * 
 * Tests the tools registry functionality and individual tool definitions
 */
import { describe, it, expect, beforeAll } from 'vitest';
import * as toolsRegistry from '../../src/tools/index.js';
import { SchemaToolProvider } from '../../src/tools/schemaTools.js';
import { MutateToolProvider } from '../../src/tools/mutateTools.js';
import { EmbeddingsToolProvider } from '../../src/tools/embeddingsTools.js';
import { GroqToolProvider } from '../../src/tools/groqTools.js';

describe('Tools', () => {
  let allTools;
  let schemaTools;
  let mutateTools;
  let embeddingsTools;
  let groqTools;
  let getTypeSchemaTool;
  let mutateDocumentTool;
  let executeGroqTool;
  let semanticSearchTool;

  beforeAll(() => {
    // Get all tools
    allTools = toolsRegistry.getToolDefinitions();

    // Get individual provider's tools
    schemaTools = new SchemaToolProvider().getToolDefinitions();
    mutateTools = new MutateToolProvider().getToolDefinitions();
    embeddingsTools = new EmbeddingsToolProvider().getToolDefinitions();
    groqTools = new GroqToolProvider().getToolDefinitions();

    // Find specific tools
    getTypeSchemaTool = schemaTools.find(tool => tool.name === 'getTypeSchema');
    mutateDocumentTool = mutateTools.find(tool => tool.name === 'mutateDocument');
    executeGroqTool = groqTools.find(tool => tool.name === 'executeGroq');
    semanticSearchTool = embeddingsTools.find(tool => tool.name === 'semanticSearch');
  });

  describe('getToolDefinitions', () => {
    it('should return an array of tool definitions', () => {
      expect(Array.isArray(allTools)).toBe(true);
      expect(allTools.length).toBeGreaterThan(0);
    });

    it('should include schema tools', () => {
      // Check if schema tools are included in the main tool registry
      const hasSchemaTools = allTools.some(tool => 
        tool.name === 'getTypeSchema'
      );
      expect(hasSchemaTools).toBe(true);

      // Additionally validate that the schema tool provider returns this tool
      expect(getTypeSchemaTool).toBeDefined();
    });

    it('should include mutate tools', () => {
      // Check if mutate tools are included in the main tool registry
      const hasMutateTools = allTools.some(tool => 
        tool.name === 'mutateDocument'
      );
      expect(hasMutateTools).toBe(true);

      // Additionally validate that the mutate tool provider returns this tool
      expect(mutateDocumentTool).toBeDefined();
    });

    it('should include GROQ tools', () => {
      // Check if GROQ tools are included in the main tool registry
      const hasGroqTools = allTools.some(tool => 
        tool.name === 'executeGroq'
      );
      expect(hasGroqTools).toBe(true);

      // Additionally validate that the GROQ tool provider returns this tool
      expect(executeGroqTool).toBeDefined();
    });

    it('should include embeddings tools', () => {
      // Check if embeddings tools are included in the main tool registry
      const hasEmbeddingsTools = allTools.some(tool => 
        tool.name === 'semanticSearch'
      );
      expect(hasEmbeddingsTools).toBe(true);

      // Additionally validate that the embeddings tool provider returns this tool
      expect(semanticSearchTool).toBeDefined();
    });
  });

  describe('getToolDefinition', () => {
    it('should return the correct tool definition by name', () => {
      const tool = toolsRegistry.getToolDefinition('getSchema');
      expect(tool).toBeDefined();
      expect(tool.name).toBe('getSchema');
    });

    it('should return null for unknown tool name', () => {
      const tool = toolsRegistry.getToolDefinition('nonExistentTool');
      expect(tool).toBeNull();
    });
  });

  describe('getTypeSchema', () => {
    it('should exist', () => {
      expect(getTypeSchemaTool).toBeDefined();
    });

    it('should have correct parameters', () => {
      const paramSchema = getTypeSchemaTool.parameters.shape;
      expect(paramSchema.typeName).toBeDefined();
      expect(paramSchema.projectId).toBeDefined();
      expect(paramSchema.dataset).toBeDefined();
    });
  });

  describe('mutateDocument', () => {
    it('should exist', () => {
      expect(mutateDocumentTool).toBeDefined();
    });

    it('should have correct parameters', () => {
      const paramSchema = mutateDocumentTool.parameters.shape;
      expect(paramSchema.mutations).toBeDefined();
      expect(paramSchema.projectId).toBeDefined();
      expect(paramSchema.dataset).toBeDefined();
      expect(paramSchema.returnDocuments).toBeDefined();
    });
  });

  describe('executeGroq', () => {
    it('should exist', () => {
      expect(executeGroqTool).toBeDefined();
    });

    it('should have correct parameters', () => {
      const paramSchema = executeGroqTool.parameters.shape;
      expect(paramSchema.query).toBeDefined();
      expect(paramSchema.projectId).toBeDefined();
      expect(paramSchema.dataset).toBeDefined();
      expect(paramSchema.params).toBeDefined();
    });
  });

  describe('semanticSearch', () => {
    it('should exist', () => {
      expect(semanticSearchTool).toBeDefined();
    });

    it('should have correct parameters', () => {
      const paramSchema = semanticSearchTool.parameters.shape;
      expect(paramSchema.query).toBeDefined();
      expect(paramSchema.indexName).toBeDefined();
      expect(paramSchema.projectId).toBeDefined();
      expect(paramSchema.dataset).toBeDefined();
      expect(paramSchema.maxResults).toBeDefined();
      expect(paramSchema.types).toBeDefined();
    });
  });
});

import { describe, it, expect, vi, beforeEach } from 'vitest';
import {
  normalizeDraftId,
  normalizeBaseDocId,
  applyPatchOperations,
  getDocumentContent,
  createErrorResponse,
  normalizeDocumentIds
} from '../../src/utils/documentHelpers';

describe('Document Helpers', () => {
  describe('normalizeDraftId', () => {
    it('should add drafts. prefix to document ID if not present', () => {
      expect(normalizeDraftId('doc123')).toBe('drafts.doc123');
    });

    it('should not modify document ID if drafts. prefix is already present', () => {
      expect(normalizeDraftId('drafts.doc123')).toBe('drafts.doc123');
    });
  });

  describe('normalizeBaseDocId', () => {
    it('should remove drafts. prefix from document ID if present', () => {
      expect(normalizeBaseDocId('drafts.doc123')).toBe('doc123');
    });

    it('should not modify document ID if drafts. prefix is not present', () => {
      expect(normalizeBaseDocId('doc123')).toBe('doc123');
    });
  });

  describe('applyPatchOperations', () => {
    let patchObj: any;

    beforeEach(() => {
      patchObj = {
        set: vi.fn(),
        setIfMissing: vi.fn(),
        unset: vi.fn(),
        inc: vi.fn(),
        dec: vi.fn(),
        insert: vi.fn(),
        diffMatchPatch: vi.fn()
      };
    });

    it('should apply set operation', () => {
      const patch = { set: { title: 'New Title' } };
      applyPatchOperations(patch, patchObj);
      expect(patchObj.set).toHaveBeenCalledWith({ title: 'New Title' });
    });

    it('should apply setIfMissing operation', () => {
      const patch = { setIfMissing: { author: 'Unknown' } };
      applyPatchOperations(patch, patchObj);
      expect(patchObj.setIfMissing).toHaveBeenCalledWith({ author: 'Unknown' });
    });

    it('should apply unset operation', () => {
      const patch = { unset: ['comments'] };
      applyPatchOperations(patch, patchObj);
      expect(patchObj.unset).toHaveBeenCalledWith(['comments']);
    });

    it('should apply inc operation', () => {
      const patch = { inc: { views: 1 } };
      applyPatchOperations(patch, patchObj);
      expect(patchObj.inc).toHaveBeenCalledWith({ views: 1 });
    });

    it('should apply dec operation', () => {
      const patch = { dec: { stock: 1 } };
      applyPatchOperations(patch, patchObj);
      expect(patchObj.dec).toHaveBeenCalledWith({ stock: 1 });
    });

    it('should apply insert operation', () => {
      const patch = { 
        insert: { 
          items: ['item1', 'item2'], 
          position: 'after', 
          at: 'tags[-1]' 
        } 
      };
      applyPatchOperations(patch, patchObj);
      expect(patchObj.insert).toHaveBeenCalledWith('after', 'tags[-1]', ['item1', 'item2']);
    });

    it('should apply diffMatchPatch operation', () => {
      const patch = { diffMatchPatch: { description: '@@ -1,5 +1,9 @@' } };
      applyPatchOperations(patch, patchObj);
      expect(patchObj.diffMatchPatch).toHaveBeenCalledWith({ description: '@@ -1,5 +1,9 @@' });
    });

    it('should apply multiple operations', () => {
      const patch = {
        set: { title: 'New Title' },
        unset: ['oldField'],
        inc: { views: 1 }
      };
      applyPatchOperations(patch, patchObj);
      expect(patchObj.set).toHaveBeenCalledWith({ title: 'New Title' });
      expect(patchObj.unset).toHaveBeenCalledWith(['oldField']);
      expect(patchObj.inc).toHaveBeenCalledWith({ views: 1 });
    });
  });

  describe('getDocumentContent', () => {
    let mockClient: any;

    beforeEach(() => {
      mockClient = {
        getDocument: vi.fn()
      };
    });

    it('should return draft document if it exists', async () => {
      const draftDoc = { _id: 'drafts.doc123', title: 'Draft Title' };
      mockClient.getDocument.mockImplementation((id) => {
        return id === 'drafts.doc123' ? draftDoc : null;
      });

      const result = await getDocumentContent(mockClient, 'doc123');

      expect(result).toEqual(draftDoc);
      expect(mockClient.getDocument).toHaveBeenCalledWith('drafts.doc123');
    });

    it('should return published document if draft does not exist', async () => {
      const publishedDoc = { _id: 'doc123', title: 'Published Title' };
      mockClient.getDocument.mockImplementation((id) => {
        return id === 'doc123' ? publishedDoc : null;
      });

      const result = await getDocumentContent(mockClient, 'doc123');

      expect(result).toEqual(publishedDoc);
      expect(mockClient.getDocument).toHaveBeenCalledWith('drafts.doc123');
      expect(mockClient.getDocument).toHaveBeenCalledWith('doc123');
    });

    it('should return fallback content if document does not exist', async () => {
      const fallbackContent = { _id: 'doc123', title: 'Fallback Title' };
      mockClient.getDocument.mockResolvedValue(null);

      const result = await getDocumentContent(mockClient, 'doc123', fallbackContent);

      expect(result).toEqual(fallbackContent);
      expect(mockClient.getDocument).toHaveBeenCalledWith('drafts.doc123');
      expect(mockClient.getDocument).toHaveBeenCalledWith('doc123');
    });

    it('should throw error if document does not exist and no fallback is provided', async () => {
      mockClient.getDocument.mockResolvedValue(null);

      await expect(getDocumentContent(mockClient, 'doc123'))
        .rejects.toThrow('Document doc123 not found');
    });
  });

  describe('createErrorResponse', () => {
    it('should create a formatted error with message', () => {
      const originalError = new Error('Database connection failed');
      const result = createErrorResponse('Failed to update document', originalError);

      expect(result.message).toBe('Failed to update document: Database connection failed');
    });

    it('should log the error to console', () => {
      const consoleSpy = vi.spyOn(console, 'error').mockImplementation(() => {});
      const originalError = new Error('Not found');

      createErrorResponse('Document retrieval error', originalError);

      expect(consoleSpy).toHaveBeenCalledWith('Document retrieval error:', originalError);
      consoleSpy.mockRestore();
    });
  });

  describe('normalizeDocumentIds', () => {
    it('should handle single string ID', () => {
      expect(normalizeDocumentIds('doc123')).toEqual(['doc123']);
    });

    it('should handle array of IDs', () => {
      expect(normalizeDocumentIds(['doc123', 'doc456'])).toEqual(['doc123', 'doc456']);
    });

    it('should parse JSON string array', () => {
      expect(normalizeDocumentIds('["doc123", "doc456"]')).toEqual(['doc123', 'doc456']);
    });

    it('should treat invalid JSON as single ID', () => {
      expect(normalizeDocumentIds('[doc123, doc456]')).toEqual(['[doc123, doc456]']);
    });

    it('should handle JSON-like string that is not actually a proper array', () => {
      expect(normalizeDocumentIds('["single"]')).toEqual(['single']);
    });

    it('should throw error for empty array', () => {
      expect(() => normalizeDocumentIds([])).toThrow('No document IDs provided');
    });

    it('should convert non-string to string', () => {
      // @ts-ignore - Testing with invalid type
      expect(normalizeDocumentIds(123)).toEqual(['123']);
    });
  });
});

import { expect, describe, it } from 'vitest';
import { isSufficientApiVersion } from '../../src/utils/sanityClient.ts';

describe('Sanity Client Utilities', () => {
  describe('isSufficientApiVersion', () => {
    it('should return true when current version is newer than required', () => {
      expect(isSufficientApiVersion('2025-03-01', '2025-02-19')).toBe(true);
      expect(isSufficientApiVersion('2026-01-01', '2025-02-19')).toBe(true);
      expect(isSufficientApiVersion('2025-02-20', '2025-02-19')).toBe(true);
    });

    it('should return true when current version equals required version', () => {
      expect(isSufficientApiVersion('2025-02-19', '2025-02-19')).toBe(true);
    });

    it('should return false when current version is older than required', () => {
      expect(isSufficientApiVersion('2024-10-01', '2025-02-19')).toBe(false);
      expect(isSufficientApiVersion('2025-01-01', '2025-02-19')).toBe(false);
      expect(isSufficientApiVersion('2025-02-18', '2025-02-19')).toBe(false);
    });

    it('should handle v-prefixed versions correctly', () => {
      expect(isSufficientApiVersion('v2025-03-01', '2025-02-19')).toBe(true);
      expect(isSufficientApiVersion('2025-03-01', 'v2025-02-19')).toBe(true);
    });
  });
});
